import React, { useEffect, useState } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import { Line } from 'react-chartjs-2';
import {
    Chart as ChartJS,
    LineElement,
    PointElement,
    Tooltip,
    Legend,
    CategoryScale,
    LinearScale,
    Title // Added Title for chart
} from 'chart.js';

// Adjust the path to your CSS file as needed
import '../styles/GrowthReport.css';

// Register required components for Chart.js
ChartJS.register(LineElement, PointElement, Tooltip, Legend, CategoryScale, LinearScale, Title);

/**
 * GrowthReport Component
 * Displays a detailed report for a single growth simulation entry.
 * Retrieves data passed via navigation state from GrowthHistory.
 */
const GrowthReport = () => {
    const navigate = useNavigate();
    const location = useLocation(); // Hook to access navigation state

    // State for the report data and derived values
    const [entryData, setEntryData] = useState(null);
    const [parsedSubstrate, setParsedSubstrate] = useState({});
    const [temperature, setTemperature] = useState('N/A');
    const [ph, setPh] = useState('N/A');
    const [yieldValue, setYieldValue] = useState(0);
    const [days, setDays] = useState(0);
    const [isLoading, setIsLoading] = useState(true);
    const [error, setError] = useState(null);

    // Map internal keys to user-friendly display names for substrate
    const substrateDisplayNames = {
        riceBran: 'Rice Bran',
        corn: 'Corn Cob',
        sugarcane: 'Sugarcane',
        sawdust: 'Sawdust',
    };

    // Effect hook to process the entry data received from navigation state
    useEffect(() => {
        setIsLoading(true);
        setError(null);

        // Check if state and entryData exist from navigation
        if (location.state && location.state.entryData) {
            const data = location.state.entryData;
            setEntryData(data);

            // --- Parse Substrate ---
            let substrateDetails = {};
            if (data.substrate) {
                if (typeof data.substrate === 'object') {
                    substrateDetails = data.substrate; // Already an object
                } else if (typeof data.substrate === 'string') {
                    try {
                        substrateDetails = JSON.parse(data.substrate);
                        if (typeof substrateDetails !== 'object' || substrateDetails === null) {
                            console.warn("Parsed substrate is not an object:", substrateDetails);
                            substrateDetails = {}; // Fallback to empty object
                        }
                    } catch (e) {
                        console.error("Failed to parse substrate JSON string:", e);
                        setError("Failed to parse substrate data.");
                        substrateDetails = {}; // Fallback
                    }
                }
            }
            setParsedSubstrate(substrateDetails);

            // --- Parse Conditions (Temp & pH) ---
            // Example assumes conditions string is like "Temp: 24°C, pH: 6.9"
            let tempVal = 'N/A';
            let phVal = 'N/A';
            if (data.conditions && typeof data.conditions === 'string') {
                const tempMatch = data.conditions.match(/Temp:\s*([\d.]+)/);
                const phMatch = data.conditions.match(/pH:\s*([\d.]+)/);
                if (tempMatch && tempMatch[1]) {
                    tempVal = tempMatch[1];
                }
                if (phMatch && phMatch[1]) {
                    phVal = phMatch[1];
                }
            }
            setTemperature(tempVal);
            setPh(phVal);

            // --- Set Yield and Days ---
            setYieldValue(Number(data.yield) || 0); // Ensure yield is a number
            setDays(Number(data.days) || 0);     // Ensure days is a number

            setIsLoading(false);
        } else {
            // Handle case where no entry data was passed
            console.error("No entry data found in navigation state.");
            setError("Could not load report data. Please go back to history and select an entry.");
            setIsLoading(false);
        }
    }, [location.state]); // Re-run effect if navigation state changes

    /**
     * Navigates back to the growth history page.
     */
    const handleBackClick = () => {
        navigate('/growth-history');
    };

    /**
     * Navigates to the landing page (home).
     */
    const handleHomeClick = () => {
        navigate('/'); // Adjust route if needed
    };

    // --- Chart Configuration (Illustrative based on final yield) ---
    // NOTE: This chart uses hardcoded labels and calculates points based
    // solely on the final yield. It does NOT represent the actual simulation curve.
    const lineData = {
        // Example labels, adjust if needed based on typical simulation lengths
        labels: ['Day 0', `Day ${Math.round(days * 0.5)}`, `Day ${Math.round(days * 0.75)}`, `Day ${days}`],
        datasets: [
            {
                label: 'Illustrative Growth', // Label for the dataset
                // Example data points: 0 at start, rises towards the final yield
                data: [
                    0, // Start at 0
                    yieldValue * 0.6, // Example intermediate point
                    yieldValue * 0.85, // Example intermediate point
                    yieldValue // End at final yield
                ],
                borderColor: 'lightgreen', // Line color
                backgroundColor: 'rgba(144, 238, 144, 0.2)', // Optional fill color
                pointRadius: 5,
                pointHoverRadius: 7,
                tension: 0.1 // Slight curve
            },
        ],
    };

    const lineOptions = {
        responsive: true,
        maintainAspectRatio: false, // Allow chart to resize height
        plugins: {
            legend: { display: false }, // Hide legend as per example
            tooltip: {
                callbacks: {
                    // Customize tooltip - shows Yield value at the point
                    label: (context) => `Yield: ${context.formattedValue}g`,
                },
            },
            title: {
                display: true,
                text: 'Illustrative Yield Curve', // Chart title
                color: '#FFF', // White text color for title
                font: { size: 18, weight: 'normal' }, // Adjusted font size
                padding: { top: 10, bottom: 20 }
            },
        },
        scales: {
            x: {
                title: { display: true, text: 'Days', color: '#FFF', font: { size: 14 } },
                ticks: { color: '#FFF' }, // White ticks
                grid: { color: 'rgba(255, 255, 255, 0.2)' } // Light grid lines
            },
            y: {
                title: { display: true, text: 'Yield (g)', color: '#FFF', font: { size: 14 } },
                ticks: { color: '#FFF' }, // White ticks
                grid: { color: 'rgba(255, 255, 255, 0.2)' }, // Light grid lines
                beginAtZero: true // Start Y axis at 0
            },
        },
    };
    // --- End Chart Configuration ---

    // --- Render Logic ---
    if (isLoading) {
        return <div className="growth-report loading"><p>Loading report...</p></div>;
    }

    if (error || !entryData) {
        return (
            <div className="growth-report error-page">
                 <header className="report-header">
                    <button className="back-button" onClick={handleBackClick}> &larr; </button>
                    <h1>Error</h1>
                 </header>
                 <main className="report-content">
                    <p className="error-message">{error || "Report data not found."}</p>
                    <button className="home-button" onClick={handleHomeClick}> Home </button>
                 </main>
            </div>
        );
    }

    // Main component render
    return (
        <div className="growth-report">
            <header className="report-header">
                <button className="back-button" onClick={handleBackClick} title="Back to History">
                    &larr;
                </button>
                {/* Display Batch Name if available */}
                <h1>Growth Report {entryData.batchName && entryData.batchName !== 'N/A' ? ` - ${entryData.batchName}` : ''}</h1>
            </header>
            <main className="report-content">
                {/* Yield Chart Section */}
                <div className="yield-chart">
                    {/* Chart title moved to chart options */}
                    <Line data={lineData} options={lineOptions} />
                </div>

                {/* Conditions Table Section */}
                <div className="chosen-conditions">
                    <h3>Conditions Summary:</h3>
                    <table>
                        <tbody>
                            {/* Display Start/End Dates */}
                            <tr>
                                <td>Start Date</td>
                                <td>{formatDate_DDMMYYYY(entryData.startDate) ?? 'N/A'}</td>
                            </tr>
                             <tr>
                                <td>End Date</td>
                                <td>{entryData.endDate ?? 'N/A'}</td>
                            </tr>
                            {/* Display Duration */}
                            <tr>
                                <td>Duration</td>
                                <td>{days} days</td>
                            </tr>
                            {/* Display Temperature */}
                            <tr>
                                <td>Temperature</td>
                                <td>{temperature}°C</td>
                            </tr>
                            {/* Display pH */}
                            <tr>
                                <td>pH</td>
                                <td>{ph}</td>
                            </tr>
                            {/* Dynamically list substrate components */}
                            {Object.entries(parsedSubstrate).map(([key, value]) => {
                                const displayName = substrateDisplayNames[key] || key.charAt(0).toUpperCase() + key.slice(1);
                                const amount = Number(value);
                                return (
                                    <tr key={key}>
                                        <td>{displayName}</td>
                                        <td>{isNaN(amount) ? value : amount + 'g'}</td>
                                    </tr>
                                );
                            })}
                            {/* Display Final Yield */}
                            <tr>
                                <td>Final Yield</td>
                                <td>{yieldValue}g</td>
                            </tr>
                            {/* Display Date Saved */}
                             <tr>
                                <td>Date Saved</td>
                                <td>{entryData.date ?? 'N/A'}</td>
                            </tr>
                        </tbody>
                    </table>
                </div>

                {/* Home Button */}
                <button className="home-button" onClick={handleHomeClick}>
                    Home
                </button>
            </main>
        </div>
    );
};

export default GrowthReport;
