import React from 'react'; // Removed unused useState, useEffect
import { useNavigate } from 'react-router-dom';
import '../styles/LandingPage.css'; // Can remove if not using this file

const LandingPage = () => {
    const navigate = useNavigate();
    // Removed hasHistory state and useEffect - both buttons are now always visible

    const handleStartClick = () => {
        navigate('/condition-setting');
    };

    const handleViewHistoryClick = () => {
        navigate('/growth-history'); // Navigate to the GrowthHistory page
    };

    // Placeholder links - replace # with actual paths
    const navLinks = [
        { name: 'New Simulation', href: '/condition-setting' },
        { name: 'Growth History', href: '/growth-history' },
        { name: 'City Sprouts', href: 'https://citysprouts.com.sg' },
        { name: 'Vidacity', href: 'https://vidacity.com.sg' },
    ];

    return (

        <div
            className="landing-page flex flex-col min-h-screen bg-cover bg-center text-white"
            // *** REPLACE WITH YOUR ACTUAL IMAGE PATH ***
            style={{ backgroundImage: "url('../assets/images/bg-new.png')" }}
        >
            {/* Optional overlay for text readability */}
            <div className="absolute inset-0 bg-black/40 z-0"></div>

            {/* Top Navigation */}
            <nav className="relative z-10 flex justify-between items-center p-4 md:p-6 bg-black/20 backdrop-blur-sm">
                {/* Logo Placeholder */}
                <div className="text-lg font-bold">
                    CITY SPROUTS {/* Replace with actual logo if available */}
                </div>
                {/* Nav Links */}
                <div className="space-x-4 md:space-x-6">
                    {navLinks.map((link) => (
                        <a key={link.name} href={link.href} className="text-sm hover:text-gray-300 transition duration-200">
                            {link.name}
                        </a>
                    ))}
                </div>
            </nav>

            {/* Main Content - Centered */}
            <main className="relative z-10 flex-grow flex flex-col justify-center items-center text-center px-4">
                <h1 className="text-4xl md:text-6xl font-bold mb-2 text-shadow-md"> {/* Added text shadow */}
                    Mushroom Growth Simulator
                </h1>
                <p className="text-lg md:text-xl text-gray-200 mb-8 text-shadow">
                    Powered By: The Mushroom People
                </p>
                {/* Action Buttons */}
                <div className="flex space-x-4">
                    <button
                        className="bg-white/80 hover:bg-white text-gray-800 font-semibold py-2 px-6 rounded-full shadow-md transition duration-200 backdrop-blur-sm"
                        onClick={handleViewHistoryClick}
                    >
                        Growth History
                    </button>
                    <button
                        className="bg-lime-300/80 hover:bg-lime-300 text-green-900 font-semibold py-2 px-6 rounded-full shadow-md transition duration-200 backdrop-blur-sm"
                        onClick={handleStartClick}
                    >
                        Start Growing
                    </button>
                </div>
            </main>
        </div>
    );
};

export default LandingPage;
