import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { Line } from 'react-chartjs-2';
import { Chart as ChartJS, LineElement, PointElement, Tooltip, Legend, CategoryScale, LinearScale, Title } from 'chart.js';
import odeRK4 from 'ode-rk4';
import '../styles/GrowthSimulation.css'; // Import the CSS file
import logoSrc from '../assets/images/cslogo.png';

// --- Simulation Logic (Keep outside component - NO CHANGES NEEDED HERE) ---
// ... (linearInterpolate, defaultParams, derivatives, getInitialState, runSimulation) ...
// (Same as before)

// Register required components for Chart.js
ChartJS.register(LineElement, PointElement, Tooltip, Legend, CategoryScale, LinearScale, Title);

// --- React Component ---
const GrowthSimulation = () => {
    const navigate = useNavigate();
    const [simulationResults, setSimulationResults] = useState(null);
    const [isLoading, setIsLoading] = useState(true);
    const [error, setError] = useState(null);
    const [batchName, setBatchName] = useState('');
    const [startDate, setStartDate] = useState('');
    const [initialCNRatio, setInitialCNRatio] = useState(defaultParams['Initial C:N Ratio']);
    const [finalSimulatedYield, setFinalSimulatedYield] = useState(null);
    const [activeChartTab, setActiveChartTab] = useState('yield'); // 'yield', 'temp'

    useEffect(() => {
        // --- useEffect Logic (NO CHANGES NEEDED HERE) ---
        // ... (Same loading and simulation logic as before) ...
        let isMounted = true;
        setIsLoading(true);
        setError(null);
        setFinalSimulatedYield(null);

        setTimeout(() => {
            if (!isMounted) return;
            try {
                const savedTemperature = localStorage.getItem('temperature');
                const savedPh = localStorage.getItem('ph');
                const savedSubstrateJson = localStorage.getItem('substrate');
                const savedDays = localStorage.getItem('simulationDays');
                const savedBatchName = localStorage.getItem('batchName') || 'N/A';
                const savedStartDate = localStorage.getItem('startDate') || 'N/A';
                const savedCNRatio = localStorage.getItem('initialCNRatio') || defaultParams['Initial C:N Ratio'];

                setBatchName(savedBatchName);
                setStartDate(savedStartDate);
                setInitialCNRatio(parseFloat(savedCNRatio));

                if (!savedTemperature || !savedPh || !savedSubstrateJson || !savedDays) {
                    throw new Error("Required simulation parameters not found in localStorage.");
                }

                const substrateData = JSON.parse(savedSubstrateJson);
                if (typeof substrateData !== 'object' || substrateData === null) {
                       throw new Error("Invalid substrate data found in localStorage.");
                }

                const temp = parseFloat(savedTemperature);
                const phValue = parseFloat(savedPh);
                const days = parseInt(savedDays, 10);
                const initialWeight = Object.values(substrateData).reduce((sum, val) => sum + (Number(val) || 0), 0);

                const simulationDuration = (Number.isInteger(days) && days > 0) ? days : defaultParams.timeLength;

                if (isNaN(temp) || isNaN(phValue) || isNaN(initialWeight)) {
                    throw new Error("Invalid numerical parameters loaded from localStorage.");
                }

                const simulationInputs = {
                    'External Temp': temp,
                    'pH': phValue,
                    'Initial Dry Substrate Weight': initialWeight > 0 ? initialWeight : defaultParams['Initial Dry Substrate Weight'],
                    'timeLength': simulationDuration,
                    'Initial C:N Ratio': parseFloat(savedCNRatio) || defaultParams['Initial C:N Ratio']
                };

                const results = runSimulation(simulationInputs);
                if (isMounted) {
                    setSimulationResults(results);
                    if (results && results.length > 0) {
                           const lastYield = results[results.length - 1]?.MushroomYield;
                           if (Number.isFinite(lastYield)) {
                                setFinalSimulatedYield(lastYield);
                           }
                    }
                }

            } catch (err) {
                console.error("Error during simulation setup or run:", err);
                 if (isMounted) setError(err.message || "Failed to run simulation.");
            } finally {
                 if (isMounted) setIsLoading(false);
            }
        }, 50);

        return () => { isMounted = false; };
    }, []); // Dependency array is empty, runs once on mount

    const handleBackClick = () => { navigate('/condition-setting'); };

    // --- Chart Data & Options ---
    // Minor adjustment: Use CSS variables conceptually if possible, or hardcode colors
    const getChartConfig = () => {
        if (!simulationResults || simulationResults.length === 0) {
            return { data: { datasets: [] }, options: {} };
        }

        // Define colors (could also try to read CSS vars if needed, but hardcoding is simpler here)
        const yieldColor = 'green'; // Matches CSS --chart-yield
        const tempColor = 'red'; // Matches CSS --chart-temp
        const textColor = '#452c0e'; // Matches CSS --brown (or --brand-brown if preferred)
        const gridColor = 'rgba(107, 79, 75, 0.1)'; // Example grid color

        let datasets = [];
        const options = {
            responsive: true, maintainAspectRatio: false,
            plugins: {
                legend: { display: true, labels: { color: textColor } }, // Use textColor
                tooltip: { enabled: true /* mode: 'index', intersect: false */ },
                title: { display: true, text: '', color: textColor, font: { size: 16 } } // Use textColor
            },
            scales: {
                x: {
                    type: 'linear',
                    title: { display: true, text: 'Time (Days)', color: textColor }, // Use textColor
                    ticks: { stepSize: 5, color: textColor }, // Use textColor
                    grid: { color: gridColor }, // Use gridColor
                    min: 0,
                },
                // Define potential Y axes
                yYield: { type: 'linear', position: 'left', title: { display: true, text: 'Yield (g)', color: yieldColor }, ticks: { color: yieldColor, stepSize: 10 }, grid: { drawOnChartArea: false }, beginAtZero: true, display: false },
                yTemp: { type: 'linear', position: 'left', title: { display: true, text: 'Temperature (째C)', color: tempColor }, ticks: { color: tempColor, stepSize: 2 }, grid: { drawOnChartArea: false }, beginAtZero: false, display: false }
            }
        };

        switch (activeChartTab) {
            case 'yield':
                options.plugins.title.text = 'Yield Over Time';
                options.scales.yYield.display = true;
                datasets = [{
                    label: 'Mushroom Yield (g)',
                    data: simulationResults.map(p => ({ x: p.time, y: Number.isFinite(p.MushroomYield) ? p.MushroomYield : null })),
                    borderColor: yieldColor, backgroundColor: 'rgba(0, 128, 0, 0.1)', // Corresponding light color
                    yAxisID: 'yYield',
                }];
                break;
            case 'temp':
                 options.plugins.title.text = 'Temperature Over Time';
                 options.scales.yTemp.display = true;
                 datasets = [{
                     label: 'Temperature (째C)',
                     data: simulationResults.map(p => ({ x: p.time, y: Number.isFinite(p.Temp) ? p.Temp : null })),
                     borderColor: tempColor, backgroundColor: 'rgba(255, 0, 0, 0.1)', // Corresponding light color
                     yAxisID: 'yTemp',
                 }];
                 break;
            default:
                datasets = [];
        }

        return {
             data: { datasets: datasets.map(ds => ({ ...ds, tension: 0.1, pointRadius: 2, spanGaps: true })) },
             options: options
        };
    };

    const chartConfig = getChartConfig();

    return (
        <div className="growth-simulation"> {/* Base container class */}
            <div className="logobox">
                <div className="logo">
                    <img src={logoSrc} alt="Logo" />
                </div>
                {/* Optional: Add Back Button Here if needed */}
                {/* <button onClick={handleBackClick} className="back-button-style">Back</button> */}
            </div>
            <div className="batch-info"> {/* Replaced Tailwind classes */}
                <span>Batch: #{batchName}</span>
                <span>Start Date: {startDate}</span>
            </div>

            {/* Main Content Grid */}
            <main className="main-content-grid"> {/* Replaced Tailwind classes */}
                {/* Left Column: Animation Placeholder */}
                <div className="animation-area content-card"> {/* Replaced Tailwind classes + common card class */}
                    <span className="animation-placeholder-text">Animation Area</span> {/* Replaced Tailwind classes */}
                </div>

                {/* Right Column: Tabs, Chart, Results */}
                <div className="chart-results-column"> {/* Replaced Tailwind classes */}
                    {/* Tabs */}
                    <div className="tabs-container"> {/* Replaced Tailwind classes */}
                        <button
                            onClick={() => setActiveChartTab('yield')}
                            className={`tab-button ${activeChartTab === 'yield' ? 'tab-button-active' : ''}`} // Conditional active class
                        >
                            Yield over Time
                        </button>
                        <button
                            onClick={() => setActiveChartTab('temp')}
                             className={`tab-button ${activeChartTab === 'temp' ? 'tab-button-active' : ''}`} // Conditional active class
                        >
                            Temperature
                        </button>
                        {/* Removed pH tab button */}
                    </div>

                    {/* Chart Area */}
                    <div className="chart-area"> {/* Replaced Tailwind classes */}
                        {isLoading && <p className="loading-message">Loading and running simulation...</p>} {/* Replaced Tailwind classes */}
                        {error && <p className="error-message">Error: {error}</p>} {/* Replaced Tailwind classes */}
                        {!isLoading && !error && simulationResults && (
                             chartConfig.data.datasets.length > 0 ? (
                                 <div className="chart-container" style={{ position: 'relative', height: '35vh', width: '100%' }}>
                                     {/* Keep inline style for chart wrapper size/position */}
                                     <Line options={chartConfig.options} data={chartConfig.data} />
                                 </div>
                             ) : (
                                  <p className="no-data-message">No data available for this chart.</p> // Replaced Tailwind
                             )
                        )}
                        {!isLoading && !error && !simulationResults && <p className="no-data-message">No simulation results available.</p>} {/* Replaced Tailwind */}
                        {/* Warning */}
                        <p className="warning-message"> {/* Replaced Tailwind classes */}
                            Warning: Simulation logic is based on a scaffold. Ensure all equations in the 'derivatives' function are fully implemented for accurate results.
                        </p>
                    </div>

                    {/* Results Section */}
                    <div className="results-section content-card"> {/* Replaced Tailwind classes + common card class */}
                        <h3 className="results-heading"> {/* Replaced Tailwind classes */}
                            Results
                            <span className="info-icon" title="Key simulation outputs based on initial conditions">(i)</span> {/* Replaced Tailwind classes */}
                        </h3>
                        <div className="results-items"> {/* Replaced Tailwind classes */}
                            <div className="result-item"> {/* Replaced Tailwind classes */}
                                <span className="result-label">Projected Yield:</span> {/* Replaced Tailwind classes */}
                                <span className="result-value"> {/* Replaced Tailwind classes */}
                                    {isLoading ? '...' : error ? 'Error' : Number.isFinite(finalSimulatedYield) ? `${finalSimulatedYield.toFixed(2)}g` : 'N/A'}
                                </span>
                            </div>
                            <div className="result-item"> {/* Replaced Tailwind classes */}
                                <span className="result-label">Initial C:N Ratio:</span> {/* Replaced Tailwind classes */}
                                <span className="result-value"> {/* Replaced Tailwind classes */}
                                    {isLoading ? '...' : error ? 'Error' : initialCNRatio.toFixed(2)}
                                </span>
                            </div>
                        </div>
                    </div>
                </div>
            </main>
        </div>
    );
};

export default GrowthSimulation;
