import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { Line } from 'react-chartjs-2';
import { Chart as ChartJS, LineElement, PointElement, Tooltip, Legend, CategoryScale, LinearScale, Title } from 'chart.js';
import odeRK4 from 'ode-rk4';
import '../styles/GrowthSimulation.css'; // Import the CSS file
import logoSrc from '../assets/images/cslogo.png';

// --- Simulation Logic & ChartJS Registration ---

// *** Default Parameters for MUSHROOM Growth (Based on original Model.json) ***
const defaultParamsMushroom = {
  timeStart: 0, timeLength: 100, timeStep: 0.2,
  'C BD Rate': 1.11, 'C Coeff': 0.7, 'C Cons. Rate': 76.39, 'CO2 Coeff': 0.8,
  'CO2 Production Rate': 410000, 'CO2 Removal Rate': 12.6, 'External CO2': 428.23,
  'External Temp': 24, 'Growth Coeff': 5.1,
  // 'Initial C:N Ratio': 43.58, // Overridden by FIXED_CN_RATIO
  'Initial Complex:Simple Ratio': 0.8054,
  'Initial Dry Substrate Weight': 410.2,
  'IsMush': 1, // <<< Key difference: Mushroom phase
  'Mushroom Multiplier': 5.17,
  'N Coeff': 68.4, 'N Rate': 13.03,
  'pH': 6.91, 'pH Coeff': 1.4, 'Temp Coeff': 1.3, 'Temp Production Rate': 405,
  'Temp Transfer Rate': 1.5,
  // Effect Data arrays (assuming they are the same for both models)
  'Effect of CO2 on Mush Growth Data': [[500, 0.9], [800, 1], [1000, 0.8], [1200, 0.25], [1400, 0]],
  'Effect of CO2 on Myc Growth Data': [[15000, 1], [20000, 0.9], [25000, 0.25], [30000, 0]],
  'Effect of pH on Mush Growth Data': [[5.5, 0], [6, 0.25], [6.7, 1], [7, 0.6], [7.5, 0]],
  'Effect of pH on Myc Growth Data': [[5.8, 0], [6, 0.25], [6.7, 1], [7, 0.6], [7.5, 0]],
  'Effect of Temp on Mush Growth Data': [[10, 0], [15, 5.37], [20, 6.36], [25, 8.63], [30, 6.37], [40, 0]],
  'Effect of Temp on Myc Growth Data': [[10, 0], [15, 5.37], [20, 6.36], [25, 8.63], [30, 6.37], [35, 5.36], [45, 0]],
};

// *** Default Parameters for MYCELIUM Growth (Based on Model (1).json) ***
const defaultParamsMycelium = {
  timeStart: 0, timeLength: 100, timeStep: 0.2,
  'C BD Rate': 1.11, 'C Coeff': 0.7, 'C Cons. Rate': 76.39, 'CO2 Coeff': 0.8,
  'CO2 Production Rate': 410000, 'CO2 Removal Rate': 1, // Updated
  'External CO2': 428.23, 'External Temp': 28, // Updated
  'Growth Coeff': 5.4, // Updated
  // 'Initial C:N Ratio': 44.3652, // Overridden by FIXED_CN_RATIO
  'Initial Complex:Simple Ratio': 0.867, // Updated
  'Initial Dry Substrate Weight': 479.975, // Updated
  'IsMush': 0, // <<< Key difference: Mycelium phase
  'Mushroom Multiplier': 26, // Updated
  'N Coeff': 68.4, 'N Rate': 1.53, // Updated
  'pH': 6.91, 'pH Coeff': 1.4, 'Temp Coeff': 1.7, // Updated
  'Temp Production Rate': 255, // Updated
  'Temp Transfer Rate': 1.5,
  // Effect Data arrays (assuming same)
  'Effect of CO2 on Mush Growth Data': [[500, 0.9], [800, 1], [1000, 0.8], [1200, 0.25], [1400, 0]],
  'Effect of CO2 on Myc Growth Data': [[15000, 1], [20000, 0.9], [25000, 0.25], [30000, 0]],
  'Effect of pH on Mush Growth Data': [[5.5, 0], [6, 0.25], [6.7, 1], [7, 0.6], [7.5, 0]],
  'Effect of pH on Myc Growth Data': [[5.8, 0], [6, 0.25], [6.7, 1], [7, 0.6], [7.5, 0]],
  'Effect of Temp on Mush Growth Data': [[10, 0], [15, 5.37], [20, 6.36], [25, 8.63], [30, 6.37], [40, 0]],
  'Effect of Temp on Myc Growth Data': [[10, 0], [15, 5.37], [20, 6.36], [25, 8.63], [30, 6.37], [35, 5.36], [45, 0]],
};

// FIXED C:N Ratio as previously requested
const FIXED_CN_RATIO = 0.96;

// --- Simulation Functions ---
function linearInterpolate(data, x) { /* ... (same as before) ... */ }
function derivatives(dydt, y, t, currentParams) { /* ... (same as before, uses currentParams) ... */ }

// Initial State Calculation - Modified to accept initial Mycelium/Space values
function getInitialState(p, initialMycelium = 0.01) { // Default initial mycelium low
  const initialDrySubstrateWeight = Number(p['Initial Dry Substrate Weight']) || defaultParamsMushroom['Initial Dry Substrate Weight']; // Fallback to one default
  const cnRatio = Number(p['Initial C:N Ratio']); // Uses the fixed ratio passed in p
  const complexSimpleRatio = Number(p['Initial Complex:Simple Ratio']) || defaultParamsMushroom['Initial Complex:Simple Ratio']; // Fallback

  const cnRatioPlusOne = cnRatio + 1;
  const Init_C = initialDrySubstrateWeight - (initialDrySubstrateWeight / cnRatioPlusOne);
  const initialSpace = 1 - initialMycelium;

  // Initial state vector: [CO2, ComplexC, MushroomYield, Mycelium, N, SimpleC, Space, Temp]
  return [
      Number(p['External CO2']) || defaultParamsMushroom['External CO2'], // Use External CO2 from params
      Init_C * complexSimpleRatio,
      0,                          // MushroomYield always starts at 0
      initialMycelium,            // << Use passed initial Mycelium
      initialDrySubstrateWeight / cnRatioPlusOne,
      Init_C * (1 - complexSimpleRatio),
      initialSpace,               // << Use calculated initial Space
      Number(p['External Temp']) || defaultParamsMushroom['External Temp'] // Temp
  ];
}

// Run Simulation Function - Modified to accept default params set
function runSimulation(inputParams, defaultParamSet) {
  const currentParams = {
      ...defaultParamSet, // Use the specified default set
      ...inputParams,   // Override with inputs from localStorage
      'Initial C:N Ratio': FIXED_CN_RATIO, // Force the C:N Ratio
  };

  // Determine initial mycelium based on which phase we are simulating
  // Use the specific default if available, otherwise use a sensible default (low for myc, higher for mush)
  const initialMyceliumValue = defaultParamSet['IsMush'] === 0
      ? (defaultParamsMycelium['Initial Mycelium'] ?? 0.004) // From Model(1).json if specified, else 0.004
      : (defaultParamsMushroom['Initial Mycelium'] ?? 0.9); // Higher starting point for mushroom phase assumed

  const y0 = getInitialState(currentParams, initialMyceliumValue); // Pass initial mycelium

  const t0 = currentParams.timeStart;
  const dt = currentParams.timeStep;
  const timeLength = Math.max(0, currentParams.timeLength);
  const positiveDt = Math.max(dt, 1e-6);
  const numSteps = Math.floor(timeLength / positiveDt);
  const derivativeFuncForSolver = (dydt, y, t) => {
      try { derivatives(dydt, y, t, currentParams); }
      catch (e) { console.error("Error in derivatives:", e); dydt.fill(0); }
  };
  const integrator = odeRK4(y0, derivativeFuncForSolver, t0, positiveDt);
  const results = [{ time: integrator.t, state: [...integrator.y] }];
  // console.log(`Starting simulation (${defaultParamSet['IsMush'] === 0 ? 'Mycelium' : 'Mushroom'}) for ${timeLength} days with params:`, currentParams);
  if (numSteps <= 0) { console.warn("Sim length/step 0."); return []; }
  for (let i = 0; i < numSteps; i++) {
       try { integrator.step(); if (integrator.y.some(val => !Number.isFinite(val))) { console.error("Sim unstable t=", integrator.t); } results.push({ time: integrator.t, state: [...integrator.y] }); }
       catch (solverError) { console.error("RK4 step error:", solverError); break; }
  }
  // console.log(`Simulation (${defaultParamSet['IsMush'] === 0 ? 'Mycelium' : 'Mushroom'}) finished.`);
  return results.map(p => ({ time: p.time, CO2: p.state[0], ComplexC: p.state[1], MushroomYield: p.state[2], Mycelium: p.state[3], N: p.state[4], SimpleC: p.state[5], Space: p.state[6], Temp: p.state[7] }));
}
// --- (End of Simulation Functions) ---

// Register ChartJS components
ChartJS.register(LineElement, PointElement, Tooltip, Legend, CategoryScale, LinearScale, Title);


// *** NEW: Helper function to format the date ***
function formatDate_DDMMYYYY(dateString) {
  // Input is expected to be YYYY-MM-DD or potentially empty/null/"N/A"
  if (!dateString || dateString === 'N/A' || !/^\d{4}-\d{2}-\d{2}$/.test(dateString)) {
      return 'N/A'; // Return 'N/A' if input is invalid or not in expected format
  }
  try {
      const [year, month, day] = dateString.split('-');
      return `${day}-${month}-${year}`; // Rearrange to DD-MM-YYYY
  } catch (e) {
      console.error("Error formatting date:", e);
      return 'N/A'; // Fallback on error
  }
}

// --- React Component ---
const GrowthSimulation = () => {
  const navigate = useNavigate();
  const [simulationResults, setSimulationResults] = useState(null);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);
  const [batchName, setBatchName] = useState('');
  const [startDate, setStartDate] = useState('');
  const [finalSimulatedYield, setFinalSimulatedYield] = useState(null);
  // Add 'mycelium' to possible active tabs, default to 'yield'
  const [activeChartTab, setActiveChartTab] = useState('yield'); // 'yield', 'temp', 'mycelium'

  // Consistent localStorage keys
  const LOCAL_STORAGE_KEYS = {
      TEMPERATURE: 'sim_temperature',
      PH: 'sim_ph',
      SUBSTRATE: 'sim_substrate',
      SIMULATION_DAYS: 'sim_duration',
      BATCH_NAME: 'sim_batch_name',
      START_DATE: 'sim_start_date',
  };

  useEffect(() => {
      let isMounted = true;
      setIsLoading(true);
      setError(null);
      setFinalSimulatedYield(null);

      setTimeout(() => {
          if (!isMounted) return;
          try {
              // Load parameters (Batch Name and Start Date are correctly loaded here)
              const savedTemperature = localStorage.getItem(LOCAL_STORAGE_KEYS.TEMPERATURE);
              const savedPh = localStorage.getItem(LOCAL_STORAGE_KEYS.PH);
              const savedSubstrateJson = localStorage.getItem(LOCAL_STORAGE_KEYS.SUBSTRATE);
              const savedDays = localStorage.getItem(LOCAL_STORAGE_KEYS.SIMULATION_DAYS);
              const savedBatchName = localStorage.getItem(LOCAL_STORAGE_KEYS.BATCH_NAME) || 'N/A'; // Correctly reads batch name
              const savedStartDate = localStorage.getItem(LOCAL_STORAGE_KEYS.START_DATE) || 'N/A'; // Correctly reads start date

              setBatchName(savedBatchName); // State is set
              setStartDate(savedStartDate); // State is set

              // --- Parameter validation and simulation run (same as before) ---
              if (savedTemperature === null || savedPh === null || savedSubstrateJson === null || savedDays === null) {
                  const missing = [ /* ... error message construction ... */ ].filter(Boolean).join(', ');
                  throw new Error(`Missing required simulation parameters: ${missing}. Please set them in Condition Settings.`);
              }
              const temp = parseFloat(savedTemperature);
              const phValue = parseFloat(savedPh);
              const days = parseInt(savedDays, 10);
              let substrateData = null;
              let initialWeight = 0;
              try {
                  substrateData = JSON.parse(savedSubstrateJson);
                  if (typeof substrateData !== 'object' || substrateData === null) throw new Error("Invalid substrate format.");
                  initialWeight = Object.values(substrateData).reduce((sum, val) => sum + (Number(val) || 0), 0);
              } catch (parseError) { /* ... error handling ... */ throw parseError; }
              if (isNaN(temp) || isNaN(phValue) || isNaN(initialWeight) || isNaN(days)) throw new Error("Invalid numerical parameters.");
              const simulationDuration = (days > 0) ? days : defaultParams.timeLength;
              const simulationInputs = {
                  'External Temp': temp,
                  'pH': phValue,
                  'Initial Dry Substrate Weight': initialWeight > 0 ? initialWeight : defaultParams['Initial Dry Substrate Weight'],
                  'timeLength': simulationDuration,
              };
              const results = runSimulation(simulationInputs);
              // --- (End of parameter validation and simulation run) ---


              if (isMounted) {
                   setSimulationResults(results);
                   if (results && results.length > 0) {
                       const lastResult = results[results.length - 1];
                        if (lastResult && Number.isFinite(lastResult.MushroomYield)) {
                            setFinalSimulatedYield(lastResult.MushroomYield);
                        }
                        console.log("Simulation ended at t=", lastResult.time, " Final State:", lastResult);
                   } else {
                        console.log("Simulation produced no results.");
                   }
               }

          } catch (err) {
              console.error("Error during simulation setup or run:", err);
              if (isMounted) setError(err.message || "Failed to run simulation.");
          } finally {
              if (isMounted) setIsLoading(false);
          }
      }, 50);

      return () => { isMounted = false; };
  }, []);

  const handleBackClick = () => { navigate('/condition-setting'); };

  // --- Chart Data & Options ---
  const getChartConfig = () => {
      if (!simulationResults || simulationResults.length === 0) {
          return { data: { datasets: [] }, options: {} };
      }

      const yieldColor = '#2E7D32';
      const tempColor = '#C62828';
      const myceliumColor = '#1976D2'; // Blue for Mycelium
      const textColor = '#333';
      const gridColor = 'rgba(0, 0, 0, 0.08)';

      let datasets = [];
      const options = {
          responsive: true, maintainAspectRatio: false,
          plugins: { /* ... legend, tooltip, title config ... */
              legend: { display: true, labels: { color: textColor, font: { family: 'Lexend'} } },
              tooltip: { enabled: true, bodyFont: { family: 'Lexend'}, titleFont: { family: 'Lexend' } },
              title: { display: true, text: '', color: textColor, font: { size: 16, family: 'Lexend' } }
           },
          scales: {
              x: { /* ... x-axis config ... */
                  type: 'linear', title: { display: true, text: 'Time (Days)', color: textColor, font: { family: 'Lexend'} },
                  ticks: { color: textColor, font: { family: 'Lexend'} }, grid: { color: gridColor }, min: 0,
               },
              // Define ALL potential Y axes
              yYield: { type: 'linear', position: 'left', title: { display: true, text: 'Yield (g)', color: yieldColor, font: { family: 'Lexend'} }, ticks: { color: yieldColor, font: { family: 'Lexend'} }, grid: { drawOnChartArea: false }, beginAtZero: true, display: false }, // Initially hidden
              yTemp: { type: 'linear', position: 'left', title: { display: true, text: 'Temperature (째C)', color: tempColor, font: { family: 'Lexend'} }, ticks: { color: tempColor, font: { family: 'Lexend'} }, grid: { drawOnChartArea: false }, beginAtZero: false, display: false }, // Initially hidden
              yMycelium: { type: 'linear', position: 'left', title: { display: true, text: 'Mycelium Density', color: myceliumColor, font: { family: 'Lexend'} }, ticks: { color: myceliumColor, font: { family: 'Lexend'} }, grid: { drawOnChartArea: false }, beginAtZero: true, suggestedMax: 1, display: false } // Initially hidden
          }
      };

      // Reset display for all axes before setting the active one
      options.scales.yYield.display = false;
      options.scales.yTemp.display = false;
      options.scales.yMycelium.display = false;


      switch (activeChartTab) {
          case 'yield':
              options.plugins.title.text = 'Yield Over Time';
              options.scales.yYield.display = true; // Show this axis
              datasets = [{
                  label: 'Mushroom Yield (g)',
                  data: simulationResults.map(p => ({ x: p.time, y: Number.isFinite(p.MushroomYield) ? p.MushroomYield : null })),
                  borderColor: yieldColor, backgroundColor: 'rgba(46, 125, 50, 0.1)',
                  yAxisID: 'yYield',
              }];
              break;
          case 'temp':
               options.plugins.title.text = 'Temperature Over Time';
               options.scales.yTemp.display = true; // Show this axis
               datasets = [{
                   label: 'Temperature (째C)',
                   data: simulationResults.map(p => ({ x: p.time, y: Number.isFinite(p.Temp) ? p.Temp : null })),
                   borderColor: tempColor, backgroundColor: 'rgba(198, 40, 40, 0.1)',
                   yAxisID: 'yTemp',
               }];
               break;
          // *** ADD CASE FOR MYCELIUM ***
          case 'mycelium':
               options.plugins.title.text = 'Mycelium Growth Over Time';
               options.scales.yMycelium.display = true; // Show this axis
               datasets = [{
                   label: 'Mycelium Density', // Or 'Mycelium Growth'
                   data: simulationResults.map(p => ({ x: p.time, y: Number.isFinite(p.Mycelium) ? p.Mycelium : null })),
                   borderColor: myceliumColor, backgroundColor: 'rgba(25, 118, 210, 0.1)',
                   yAxisID: 'yMycelium',
               }];
               break;
          default:
              datasets = [];
      }

      return {
           data: { datasets: datasets.map(ds => ({ ...ds, tension: 0.1, pointRadius: 2, spanGaps: true })) },
           options: options
      };
  };

  const chartConfig = getChartConfig();

  // --- JSX ---
  return (
       <div className="growth-simulation">
           <header className="simulation-header">
               <div className="logobox">
                   <div className="logo">
                       <img src={logoSrc} alt="City Sprouts Logo" />
                   </div>
               </div>
               {/* These values are correctly set from localStorage via useEffect */}
               <div className="batch-info">
                   <span className="batch-name">Batch: {batchName}</span>
                   <span className="start-date">Start Date: {formatDate_DDMMYYYY(startDate)}</span>
               </div>
           </header>

           <main className="main-content-grid">
               <div className="animation-area content-card">
                   <span>Animation</span>
               </div>

               <div className="chart-results-column">
                   <div className="tabs-container">
                       <button
                           onClick={() => setActiveChartTab('yield')}
                           className={`tab-button ${activeChartTab === 'yield' ? 'tab-button-active' : ''}`}
                       > Yield over Time </button>
                       <button
                           onClick={() => setActiveChartTab('temp')}
                            className={`tab-button ${activeChartTab === 'temp' ? 'tab-button-active' : ''}`}
                       > Temperature </button>
                       {/* *** ADD MYCELIUM TAB BUTTON *** */}
                       <button
                           onClick={() => setActiveChartTab('mycelium')}
                            className={`tab-button ${activeChartTab === 'mycelium' ? 'tab-button-active' : ''}`}
                       > Mycelium </button>
                   </div>

                   <div className="chart-area content-card">
                          {/* Updated Loading/Error checks */}
                          {isLoading && <p className="loading-message">Running simulations...</p>}
                          {error && <p className="error-message">Error: {error}</p>}
                          {!isLoading && !error && simulationDataReady && ( // Check if *any* data is ready
                               chartConfig.data.datasets.length > 0 && chartConfig.data.datasets[0].data.length > 0 ? ( // Check if current tab has data
                                   <div className="chart-container"> <Line options={chartConfig.options} data={chartConfig.data} /> </div>
                               ) : ( <p className="no-data-message">No data available for this chart.</p> ) // Show if specific chart has no data
                          )}
                          {!isLoading && !error && !simulationDataReady && <p className="no-data-message">Simulation results not available.</p>}
                     </div>

                   <div className="results-section content-card">
                        {/* Results Display Logic (no changes needed here) */}
                        <h3 className="results-heading"> Results <span className="info-icon" title="Key simulation outputs based on initial conditions">i</span> </h3>
                       <div className="results-items">
                           <div className="result-item">
                               <span className="result-label">Projected Yield:</span>
                               <span className="result-value">
                                   {isLoading ? '...' : error ? 'Error' : Number.isFinite(finalSimulatedYield) ? `${finalSimulatedYield.toFixed(1)}g` : 'N/A'}
                               </span>
                           </div>
                           <div className="result-item">
                               <span className="result-label">Initial C:N Ratio:</span>
                               <span className="result-value">
                                   {FIXED_CN_RATIO.toFixed(2)}
                               </span>
                           </div>
                       </div>
                   </div>
               </div>
           </main>
       </div>
   );
};

export default GrowthSimulation;
