import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { Line } from 'react-chartjs-2';
import { Chart as ChartJS, LineElement, PointElement, Tooltip, Legend, CategoryScale, LinearScale, Title } from 'chart.js';
import odeRK4 from 'ode-rk4';
import '../styles/GrowthSimulation.css'; // Ensure this path is correct
import logoSrc from '../assets/images/cslogo.png'; // Ensure this path is correct

// --- Simulation Logic & ChartJS Registration ---

// *** Default Parameters for MUSHROOM Growth (Based on original Model.json) ***
const defaultParamsMushroom = {
    timeStart: 0, timeLength: 100, timeStep: 0.2,
    'C BD Rate': 1.11, 'C Coeff': 0.7, 'C Cons. Rate': 76.39, 'CO2 Coeff': 0.8,
    'CO2 Production Rate': 410000, 'CO2 Removal Rate': 12.6, 'External CO2': 428.23,
    'External Temp': 24, 'Growth Coeff': 5.1,
    // 'Initial C:N Ratio': 43.58, // Overridden by FIXED_CN_RATIO
    'Initial Complex:Simple Ratio': 0.8054,
    'Initial Dry Substrate Weight': 410.2, // Default fallback weight
    'IsMush': 1, // <<< Mushroom phase
    'Mushroom Multiplier': 5.17,
    'N Coeff': 68.4, 'N Rate': 13.03,
    'pH': 6.91, // Default fallback pH
    'pH Coeff': 1.4, 'Temp Coeff': 1.3, 'Temp Production Rate': 405,
    'Temp Transfer Rate': 1.5,
    'Effect of CO2 on Mush Growth Data': [[500, 0.9], [800, 1], [1000, 0.8], [1200, 0.25], [1400, 0]],
    'Effect of CO2 on Myc Growth Data': [[15000, 1], [20000, 0.9], [25000, 0.25], [30000, 0]],
    'Effect of pH on Mush Growth Data': [[5.5, 0], [6, 0.25], [6.7, 1], [7, 0.6], [7.5, 0]],
    'Effect of pH on Myc Growth Data': [[5.8, 0], [6, 0.25], [6.7, 1], [7, 0.6], [7.5, 0]],
    'Effect of Temp on Mush Growth Data': [[10, 0], [15, 5.37], [20, 6.36], [25, 8.63], [30, 6.37], [40, 0]],
    'Effect of Temp on Myc Growth Data': [[10, 0], [15, 5.37], [20, 6.36], [25, 8.63], [30, 6.37], [35, 5.36], [45, 0]],
    // Define a potential default starting Mycelium for this phase if needed
    'Initial Mycelium': 0.9, // Example: Assume high colonization before fruiting
};

// *** Default Parameters for MYCELIUM Growth (Based on Model (1).json) ***
const defaultParamsMycelium = {
    timeStart: 0, timeLength: 100, timeStep: 0.2,
    'C BD Rate': 1.11, 'C Coeff': 0.7, 'C Cons. Rate': 76.39, 'CO2 Coeff': 0.8,
    'CO2 Production Rate': 410000, 'CO2 Removal Rate': 1,
    'External CO2': 428.23, 'External Temp': 28,
    'Growth Coeff': 5.4,
    'Initial C:N Ratio': 44.3652, // Overridden by FIXED_CN_RATIO
    'Initial Complex:Simple Ratio': 0.867,
    'Initial Dry Substrate Weight': 200, // Default fallback weight
    'IsMush': 0, // <<< Mycelium phase
    'Mushroom Multiplier': 26,
    'N Coeff': 68.4, 'N Rate': 1.53,
    'pH': 6.91, // Default fallback pH
    'pH Coeff': 1.4, 'Temp Coeff': 1.7,
    'Temp Production Rate': 255,
    'Temp Transfer Rate': 1.5,
    'Effect of CO2 on Mush Growth Data': [[500, 0.9], [800, 1], [1000, 0.8], [1200, 0.25], [1400, 0]],
    'Effect of CO2 on Myc Growth Data': [[15000, 1], [20000, 0.9], [25000, 0.25], [30000, 0]],
    'Effect of pH on Mush Growth Data': [[5.5, 0], [6, 0.25], [6.7, 1], [7, 0.6], [7.5, 0]],
    'Effect of pH on Myc Growth Data': [[5.8, 0], [6, 0.25], [6.7, 1], [7, 0.6], [7.5, 0]],
    'Effect of Temp on Mush Growth Data': [[10, 0], [15, 5.37], [20, 6.36], [25, 8.63], [30, 6.37], [40, 0]],
    'Effect of Temp on Myc Growth Data': [[10, 0], [15, 5.37], [20, 6.36], [25, 8.63], [30, 6.37], [35, 5.36], [45, 0]],
     // Define a potential default starting Mycelium for this phase
    'Initial Mycelium': 0.004, // From Model(1).json
};

// FIXED C:N Ratio as previously requested
const FIXED_CN_RATIO = 0.96;

// --- Simulation Functions ---

function linearInterpolate(data, x) {
    if (!data || data.length === 0) return 0;
    if (x <= data[0][0]) return data[0][1];
    if (x >= data[data.length - 1][0]) return data[data.length - 1][1];
    let i = 0;
    while (i < data.length - 1 && x > data[i + 1][0]) i++;
    const x0 = data[i][0], y0 = data[i][1], x1 = data[i + 1][0], y1 = data[i + 1][1];
    const divisor = (x1 - x0);
    if (divisor === 0) return y0;
    const t = (x - x0) / divisor;
    return y0 + t * (y1 - y0);
}

function derivatives(dydt, y, t, currentParams) {
    const [ currentCO2, currentComplexC, currentMushroomYield, currentMycelium, currentN, currentSimpleC, currentSpace, currentTemp ] = y;
    const initialDrySubstrateWeight = currentParams['Initial Dry Substrate Weight'] || 1;
    const cnRatio = currentParams['Initial C:N Ratio'];
    const cnRatioPlusOne = cnRatio + 1;
    // InitC depends on weight and *actual* C:N ratio used
    const InitC = initialDrySubstrateWeight - (initialDrySubstrateWeight / cnRatioPlusOne);
    // Available N/C depend on the current state of N and SimpleC stocks
    const AvailableN = initialDrySubstrateWeight > 0 ? currentN / initialDrySubstrateWeight : 0;
    const AvailableC = initialDrySubstrateWeight > 0 ? currentSimpleC / initialDrySubstrateWeight : 0;
    const NEffect = currentParams['N Coeff'] * AvailableN;
    const CEffect = Math.sqrt(Math.max(0, currentParams['C Coeff'] * AvailableC));
    const VolumeNormalisation = initialDrySubstrateWeight > 0 ? Math.sqrt(100 / initialDrySubstrateWeight) : Math.sqrt(100); // Avoid division by zero

    // Effects based on interpolation
    const Effect_of_CO2_on_Mush_Growth = linearInterpolate(currentParams['Effect of CO2 on Mush Growth Data'], currentCO2);
    const Effect_of_CO2_on_Myc_Growth = linearInterpolate(currentParams['Effect of CO2 on Myc Growth Data'], currentCO2);
    const Effect_of_pH_on_Mush_Growth = linearInterpolate(currentParams['Effect of pH on Mush Growth Data'], currentParams['pH']);
    const Effect_of_pH_on_Myc_Growth = linearInterpolate(currentParams['Effect of pH on Myc Growth Data'], currentParams['pH']);
    const Effect_of_Temp_on_Mush_Growth = linearInterpolate(currentParams['Effect of Temp on Mush Growth Data'], currentTemp);
    const Effect_of_Temp_on_Myc_Growth = linearInterpolate(currentParams['Effect of Temp on Myc Growth Data'], currentTemp);

    // Calculate combined effects based on IsMush flag
    const TempEff_ = currentParams['IsMush'] === 0 ? Effect_of_Temp_on_Myc_Growth : Effect_of_Temp_on_Mush_Growth;
    const TempEffect = (currentParams['Temp Coeff'] * TempEff_) / 10;
    const pHEff_ = currentParams['IsMush'] === 0 ? Effect_of_pH_on_Myc_Growth : Effect_of_pH_on_Mush_Growth;
    const pHEffect = pHEff_ * currentParams['pH Coeff'];
    const CO2Eff_ = currentParams['IsMush'] === 0 ? Effect_of_CO2_on_Myc_Growth : Effect_of_CO2_on_Mush_Growth;
    const CO2Effect = currentParams['CO2 Coeff'] * CO2Eff_;

    // Ensure effects are non-negative before combining
    const safeTempEffect = Math.max(0, TempEffect);
    const safePhEffect = Math.max(0, pHEffect);
    const safeNEffect = Math.max(0, NEffect);
    const safeCEffect = Math.max(0, CEffect); // CEffect already handles sqrt(neg) via Math.max
    const safeCO2Effect = Math.max(0, CO2Effect);

    const RawGrowthRate = safeTempEffect * safePhEffect * safeNEffect * safeCEffect * safeCO2Effect;
    const GrowthOpp = currentSpace * currentMycelium; // Space and Mycelium are state variables

    let GrowthRate = currentParams['IsMush'] === 1
        ? (RawGrowthRate * currentParams['Growth Coeff'])
        : (RawGrowthRate * GrowthOpp * currentParams['Growth Coeff']);
    GrowthRate = Math.max(0, GrowthRate); // Ensure non-negative growth rate

    let NormGrowthRate = currentParams['IsMush'] === 1 ? (GrowthRate / 100) : GrowthRate;

    // Calculate Flows
    const flow_Breakdown = Math.max(0, currentMycelium * currentParams['C BD Rate'] * currentComplexC * RawGrowthRate * VolumeNormalisation);
    const flow_CCons = Math.max(0, NormGrowthRate * currentParams['C Cons. Rate']);
    const flow_CO2Prod = Math.max(0, NormGrowthRate * currentParams['CO2 Production Rate']);
    const flow_CO2Vent = Math.max(0, currentParams['CO2 Removal Rate'] * (currentCO2 - currentParams['External CO2']) * VolumeNormalisation);
    const flow_Colonisation = currentParams['IsMush'] === 0 ? GrowthRate : 0; // Already non-negative
    const flow_MushroomGrowth = currentParams['IsMush'] === 1 ? Math.max(0, GrowthRate * currentParams['Mushroom Multiplier']) : 0;
    const flow_NCons = Math.max(0, NormGrowthRate * currentParams['N Rate']);
    const flow_TempProd = Math.max(0, NormGrowthRate * currentParams['Temp Production Rate']);
    const flow_TempVent = Math.max(0, (currentTemp - currentParams['External Temp']) * currentParams['Temp Transfer Rate'] * VolumeNormalisation);

    // Assign derivatives (rate of change for stocks)
    dydt[0] = flow_CO2Prod - flow_CO2Vent; // dCO2/dt
    dydt[1] = -flow_Breakdown; // dComplexC/dt
    dydt[2] = flow_MushroomGrowth; // dMushroomYield/dt
    dydt[3] = flow_Colonisation; // dMycelium/dt
    dydt[4] = -flow_NCons; // dN/dt
    dydt[5] = flow_Breakdown - flow_CCons; // dSimpleC/dt
    dydt[6] = -flow_Colonisation; // dSpace/dt
    dydt[7] = flow_TempProd - flow_TempVent; // dTemp/dt

    // Apply non-negative constraints AFTER calculating net change
    if (currentCO2 <= 1e-9 && dydt[0] < 0) dydt[0] = 0; // Use small threshold
    if (currentComplexC <= 1e-9 && dydt[1] < 0) dydt[1] = 0;
    if (currentMushroomYield <= 1e-9 && dydt[2] < 0) dydt[2] = 0;
    if (currentMycelium <= 1e-9 && dydt[3] < 0) dydt[3] = 0;
    if (currentN <= 1e-9 && dydt[4] < 0) dydt[4] = 0;
    if (currentSimpleC <= 1e-9 && dydt[5] < 0) dydt[5] = 0;
    if (currentSpace <= 1e-9 && dydt[6] < 0) dydt[6] = 0;
    if (currentTemp <= 1e-9 && dydt[7] < 0) dydt[7] = 0; // Allow temp to drop but not below absolute zero conceptually
}


function getInitialState(p, initialMyceliumValue) {
    const initialDrySubstrateWeight = Number(p['Initial Dry Substrate Weight']);
    const cnRatio = Number(p['Initial C:N Ratio']);
    const complexSimpleRatio = Number(p['Initial Complex:Simple Ratio']);

    // Basic validation
    if (isNaN(initialDrySubstrateWeight) || initialDrySubstrateWeight <=0 || isNaN(cnRatio) || isNaN(complexSimpleRatio)) {
        console.error("Invalid parameters for getInitialState:", p);
        // Return a default state to avoid crashing, though simulation might be meaningless
         return [400, 200, 0, 0.01, 10, 50, 0.99, 25];
    }

    const cnRatioPlusOne = cnRatio + 1;
    const Init_C = initialDrySubstrateWeight - (initialDrySubstrateWeight / cnRatioPlusOne);
    const initialSpace = Math.max(0, 1 - initialMyceliumValue); // Ensure space isn't negative

    return [
        Number(p['External CO2']),
        Init_C * complexSimpleRatio,
        0,
        initialMyceliumValue,
        initialDrySubstrateWeight / cnRatioPlusOne,
        Init_C * (1 - complexSimpleRatio),
        initialSpace,
        Number(p['External Temp'])
    ];
}


function runSimulation(inputParams, defaultParamSet) {
    // Ensure defaultParamSet is provided
    if (!defaultParamSet) {
        console.error("runSimulation called without defaultParamSet!");
        return []; // Or throw an error
    }

    // Merge parameters carefully
    const currentParams = {
        ...defaultParamSet, // Start with the specified default set
        ...inputParams,   // Override with inputs from localStorage (Temp, pH, Weight, Duration etc)
        'Initial C:N Ratio': FIXED_CN_RATIO, // Force the C:N Ratio
    };

    // Resolve final values for initial state calculation, falling back to currentParams (which includes defaults)
     const finalInitialWeight = Number(currentParams['Initial Dry Substrate Weight']);
     const finalCNRatio = Number(currentParams['Initial C:N Ratio']);
     const finalComplexSimpleRatio = Number(currentParams['Initial Complex:Simple Ratio']);
     const finalExternalCO2 = Number(currentParams['External CO2']);
     const finalExternalTemp = Number(currentParams['External Temp']);

    // Determine initial mycelium based on the phase defined in currentParams
    const initialMyceliumValue = currentParams['IsMush'] === 0
        ? (currentParams['Initial Mycelium'] ?? 0.004) // Use default from the set or fallback
        : (currentParams['Initial Mycelium'] ?? 0.9);  // Use default from the set or fallback

    // Prepare parameters needed ONLY for getInitialState
    const initialStateParams = {
        'Initial Dry Substrate Weight': finalInitialWeight,
        'Initial C:N Ratio': finalCNRatio,
        'Initial Complex:Simple Ratio': finalComplexSimpleRatio,
        'External CO2': finalExternalCO2,
        'External Temp': finalExternalTemp,
    };

    const y0 = getInitialState(initialStateParams, initialMyceliumValue);

    const t0 = currentParams.timeStart;
    const dt = currentParams.timeStep;
    const timeLength = Math.max(0, Number(currentParams.timeLength)); // Ensure timeLength is a number
    const positiveDt = Math.max(dt, 1e-6);
    const numSteps = timeLength > 0 && positiveDt > 0 ? Math.floor(timeLength / positiveDt) : 0;

    const derivativeFuncForSolver = (dydt, y, t) => {
        try { derivatives(dydt, y, t, currentParams); } // Pass the full currentParams here
        catch (e) { console.error("Error in derivatives:", e); dydt.fill(0); }
    };

    const integrator = odeRK4(y0, derivativeFuncForSolver, t0, positiveDt);
    const results = [{ time: integrator.t, state: [...integrator.y] }];

    const simType = currentParams['IsMush'] === 0 ? 'Mycelium' : 'Mushroom';
    console.log(`Starting ${simType} simulation for ${timeLength} days.`); // Removed params logging for brevity

    if (numSteps <= 0) { console.warn(`${simType} Sim: length/step 0.`); return []; }

    for (let i = 0; i < numSteps; i++) {
        try {
            integrator.step();
            if (integrator.y.some(val => !Number.isFinite(val))) {
                 console.error(`${simType} Sim: unstable t=`, integrator.t.toFixed(2));
                 // Stop potentially infinite loops if state becomes NaN
                 if (i > numSteps * 2) { // Heuristic check for runaway loop
                    console.error(`${simType} Sim: Terminating possibly infinite loop due to instability.`);
                    break;
                }
            }
            results.push({ time: integrator.t, state: [...integrator.y] });
        } catch (solverError) {
            console.error(`${simType} Sim: RK4 step error:`, solverError);
            break;
        }
    }
    console.log(`${simType} simulation finished.`);
    return results.map(p => ({ time: p.time, CO2: p.state[0], ComplexC: p.state[1], MushroomYield: p.state[2], Mycelium: p.state[3], N: p.state[4], SimpleC: p.state[5], Space: p.state[6], Temp: p.state[7] }));
}
// --- (End of Simulation Functions) ---

// Register ChartJS components
ChartJS.register(LineElement, PointElement, Tooltip, Legend, CategoryScale, LinearScale, Title);

// Helper function to format date
function formatDate_DDMMYYYY(dateString) {
     if (!dateString || dateString === 'N/A' || !/^\d{4}-\d{2}-\d{2}$/.test(dateString)) { return 'N/A'; }
     try { const [year, month, day] = dateString.split('-'); return `${day}-${month}-${year}`; }
     catch (e) { console.error("Error formatting date:", e); return 'N/A'; }
}

// --- React Component ---
const GrowthSimulation = () => {
    const navigate = useNavigate();
    const [simulationResultsMushroom, setSimulationResultsMushroom] = useState(null);
    const [simulationResultsMycelium, setSimulationResultsMycelium] = useState(null);
    const [isLoading, setIsLoading] = useState(true);
    const [error, setError] = useState(null);
    const [batchName, setBatchName] = useState('');
    const [startDate, setStartDate] = useState('');
    const [finalSimulatedYield, setFinalSimulatedYield] = useState(null);
    const [activeChartTab, setActiveChartTab] = useState('yield');

    const LOCAL_STORAGE_KEYS = {
        TEMPERATURE: 'sim_temperature',
        PH: 'sim_ph',
        SUBSTRATE: 'sim_substrate',
        SIMULATION_DAYS: 'sim_duration',
        BATCH_NAME: 'sim_batch_name',
        START_DATE: 'sim_start_date',
    };

    useEffect(() => {
        let isMounted = true;
        setIsLoading(true);
        setError(null);
        setFinalSimulatedYield(null);
        setSimulationResultsMushroom(null);
        setSimulationResultsMycelium(null);

        const runSimulationsAsync = async () => {
            await new Promise(resolve => setTimeout(resolve, 50)); // Short delay
            if (!isMounted) return;

            let savedTempStr, savedPhStr, savedSubstrateJson, savedDaysStr, savedBatchName, savedStartDate;
            try {
                // Load parameters from localStorage
                 savedTempStr = localStorage.getItem(LOCAL_STORAGE_KEYS.TEMPERATURE);
                 savedPhStr = localStorage.getItem(LOCAL_STORAGE_KEYS.PH);
                 savedSubstrateJson = localStorage.getItem(LOCAL_STORAGE_KEYS.SUBSTRATE);
                 savedDaysStr = localStorage.getItem(LOCAL_STORAGE_KEYS.SIMULATION_DAYS);
                 savedBatchName = localStorage.getItem(LOCAL_STORAGE_KEYS.BATCH_NAME) || 'N/A';
                 savedStartDate = localStorage.getItem(LOCAL_STORAGE_KEYS.START_DATE) || 'N/A';

                if (isMounted) {
                    setBatchName(savedBatchName);
                    setStartDate(savedStartDate);
                }

                // Validate required parameters BEFORE parsing
                if (savedTempStr === null || savedPhStr === null || savedSubstrateJson === null || savedDaysStr === null) {
                    const missing = [/*...*/].filter(Boolean).join(', '); // Construct missing list
                    throw new Error(`Missing required parameters: ${missing}. Please set in Condition Settings.`);
                }

                // Parse values
                const temp = parseFloat(savedTempStr);
                const phValue = parseFloat(savedPhStr);
                const days = parseInt(savedDaysStr, 10);
                let substrateData = null; let initialWeight = 0;
                try {
                    substrateData = JSON.parse(savedSubstrateJson);
                    if (typeof substrateData !== 'object' || substrateData === null) throw new Error("Invalid substrate format.");
                    initialWeight = Object.values(substrateData).reduce((sum, val) => sum + (Number(val) || 0), 0);
                    if (initialWeight <= 0) console.warn("Initial substrate weight is zero or negative.");
                } catch (parseError) { throw new Error(`Invalid substrate data: ${parseError.message}`); }

                if (isNaN(temp) || isNaN(phValue) || isNaN(initialWeight) || isNaN(days)) {
                     throw new Error("Invalid numerical parameters loaded.");
                 }

                const simulationDuration = (days > 0) ? days : defaultParamsMushroom.timeLength;

                // Base inputs common to both simulations
                const simulationInputs = {
                    'External Temp': temp,
                    'pH': phValue,
                    'Initial Dry Substrate Weight': initialWeight, // Use calculated weight directly
                    'timeLength': simulationDuration,
                };

                 // --- Run BOTH simulations ---
                 console.log("Running Mushroom Simulation...");
                 const resultsMushroom = runSimulation(
                     simulationInputs, // Pass only required overrides
                     defaultParamsMushroom // Pass mushroom defaults
                 );

                 console.log("Running Mycelium Simulation...");
                 const resultsMycelium = runSimulation(
                     simulationInputs, // Pass only required overrides
                     defaultParamsMycelium // Pass mycelium defaults
                 );

                if (isMounted) {
                    setSimulationResultsMushroom(resultsMushroom);
                    setSimulationResultsMycelium(resultsMycelium);

                    if (resultsMushroom && resultsMushroom.length > 0) {
                        const lastMushroomResult = resultsMushroom[resultsMushroom.length - 1];
                         if (lastMushroomResult && Number.isFinite(lastMushroomResult.MushroomYield)) {
                             setFinalSimulatedYield(lastMushroomResult.MushroomYield);
                         }
                    }
                    console.log("Both simulations complete.");
                }

            } catch (err) {
                console.error("Error during simulation setup or run:", err);
                if (isMounted) setError(err.message || "Failed to run simulation.");
            } finally {
                if (isMounted) setIsLoading(false);
            }
        };

        runSimulationsAsync();

        return () => { isMounted = false; };
    }, []);

    const handleBackClick = () => { navigate('/condition-setting'); };

    const getChartConfig = () => {
      const currentResultsSource = activeChartTab === 'mycelium'
          ? simulationResultsMycelium
          : simulationResultsMushroom;

      // Default options setup
      const yieldColor = '#2E7D32'; const tempColor = '#C62828'; const myceliumColor = '#1976D2';
      const textColor = '#333'; const gridColor = 'rgba(0, 0, 0, 0.08)';
      const options = {
          responsive: true, maintainAspectRatio: false,
          plugins: { /* ... legend, tooltip, title ... */
              legend: { display: true, labels: { color: textColor, font: { family: 'Lexend'} } },
              tooltip: { enabled: true, bodyFont: { family: 'Lexend'}, titleFont: { family: 'Lexend' } },
              title: { display: true, text: '', color: textColor, font: { size: 16, family: 'Lexend' } }
           },
          scales: {
              x: { type: 'linear', title: { display: true, text: 'Time (Days)', color: textColor, font: { family: 'Lexend'} }, ticks: { color: textColor, font: { family: 'Lexend'} }, grid: { color: gridColor }, min: 0, max: undefined }, // Initialize max
              yYield: { /* ... */ display: false },
              yTemp: { /* ... */ display: false },
              yMycelium: { type: 'linear', position: 'left', title: { display: true, text: 'Mycelium Density', color: myceliumColor, font: { family: 'Lexend'} }, ticks: { color: myceliumColor, font: { family: 'Lexend'} }, grid: { drawOnChartArea: false }, beginAtZero: true, suggestedMax: 1, display: false }
          }
      };
      options.scales.yYield.display = false; options.scales.yTemp.display = false; options.scales.yMycelium.display = false; // Reset

      if (!currentResultsSource || currentResultsSource.length === 0) {
          return { data: { datasets: [] }, options: options };
      }

      let datasets = [];
      let chartData = [...currentResultsSource]; // Copy source data

      switch (activeChartTab) {
          case 'yield':
              options.plugins.title.text = 'Yield Over Time (Mushroom Sim)';
              options.scales.yYield.display = true;
              datasets = [{ label: 'Mushroom Yield (g)', data: chartData.map(p => ({ x: p.time, y: Number.isFinite(p.MushroomYield) ? p.MushroomYield : null })), borderColor: yieldColor, backgroundColor: 'rgba(46, 125, 50, 0.1)', yAxisID: 'yYield', }];
              break;
          case 'temp':
               options.plugins.title.text = 'Temperature Over Time (Mushroom Sim)';
               options.scales.yTemp.display = true;
               datasets = [{ label: 'Temperature (Â°C)', data: chartData.map(p => ({ x: p.time, y: Number.isFinite(p.Temp) ? p.Temp : null })), borderColor: tempColor, backgroundColor: 'rgba(198, 40, 40, 0.1)', yAxisID: 'yTemp', }];
               break;
          case 'mycelium':
               options.plugins.title.text = 'Mycelium Growth (Mycelium Sim)';
               options.scales.yMycelium.display = true;

               // *** Logic to find cutoff point ***
               let cutoffIndex = chartData.findIndex(p => p.Mycelium >= 0.9);
               let finalTime = chartData[chartData.length - 1]?.time ?? 0; // Default to last time point
               let cutoffReason = "Simulation End"; // Default reason

               if (cutoffIndex !== -1) {
                   // Cut off at or after the 0.9 threshold is met
                   chartData = chartData.slice(0, cutoffIndex + 1);
                   finalTime = chartData[chartData.length - 1]?.time ?? finalTime; // Time at cutoff
                   cutoffReason = "Mycelium >= 0.9";
                   console.log(`Mycelium chart cutoff at index ${cutoffIndex}, time ${finalTime.toFixed(2)} (Threshold 0.9 reached)`);
               } else {
                   // Threshold 0.9 NOT met, check for day 30 cutoff
                   const day30Index = chartData.findIndex(p => p.time >= 30);

                   if (day30Index !== -1) {
                       // Cut off at or after day 30 is met
                       chartData = chartData.slice(0, day30Index + 1);
                       finalTime = chartData[chartData.length - 1]?.time ?? finalTime; // Time at day 30 (or just after)
                       cutoffReason = "Day 30 Limit";
                       console.log(`Mycelium chart cutoff at index ${day30Index}, time ${finalTime.toFixed(2)} (Day 30 limit)`);
                   } else {
                       // Less than 30 days simulated AND threshold not met, show all data
                       console.log(`Mycelium threshold 0.9 not reached and simulation ended before day 30 (t=${finalTime.toFixed(2)}). Showing all data.`);
                       // finalTime is already set to the last available time
                   }
               }

               // Set max time for X-axis
               options.scales.x.max = finalTime;

               // Set chart title indicating cutoff if not full duration
               if (cutoffReason !== "Simulation End") {
                  options.plugins.title.text += ` (Stopped at ${cutoffReason})`;
               }


               datasets = [{
                   label: 'Mycelium Density',
                   data: chartData.map(p => ({ x: p.time, y: Number.isFinite(p.Mycelium) ? p.Mycelium : null })),
                   borderColor: myceliumColor, backgroundColor: 'rgba(25, 118, 210, 0.1)', yAxisID: 'yMycelium',
               }];
               break;
          default:
              datasets = [];
      }

      return {
           data: { datasets: datasets && datasets.length > 0 ? datasets.map(ds => ({ ...ds, tension: 0.1, pointRadius: 2, spanGaps: true })) : [] },
           options: options
      };
  };

    const chartConfig = getChartConfig();
    const simulationDataReady = simulationResultsMushroom || simulationResultsMycelium;

    return (
         <div className="growth-simulation">
             <header className="simulation-header">
                 <div className="logobox">
                      <button onClick={handleBackClick} className="back-button" title="Back to Settings">&larr;</button>
                     <div className="logo"><img src={logoSrc} alt="City Sprouts Logo" /></div>
                 </div>
                 <div className="batch-info">
                     <span className="batch-name">Batch: {batchName}</span>
                     <span className="start-date">Start Date: {formatDate_DDMMYYYY(startDate)}</span>
                 </div>
             </header>
             <main className="main-content-grid">
                 <div className="animation-area content-card"><span>Animation</span></div>
                 <div className="chart-results-column">
                     <div className="tabs-container">
                          <button onClick={() => setActiveChartTab('yield')} className={`tab-button ${activeChartTab === 'yield' ? 'tab-button-active' : ''}`}> Yield over Time </button>
                          <button onClick={() => setActiveChartTab('temp')} className={`tab-button ${activeChartTab === 'temp' ? 'tab-button-active' : ''}`}> Temperature </button>
                          <button onClick={() => setActiveChartTab('mycelium')} className={`tab-button ${activeChartTab === 'mycelium' ? 'tab-button-active' : ''}`}> Mycelium </button>
                     </div>
                     <div className="chart-area content-card">
                           {isLoading && <p className="loading-message">Running simulations...</p>}
                           {error && <p className="error-message">Error: {error}</p>}
                           {!isLoading && !error && simulationDataReady && (
                                chartConfig.data.datasets.length > 0 && chartConfig.data.datasets[0].data.length > 0 ? (
                                    <div className="chart-container"> <Line options={chartConfig.options} data={chartConfig.data} /> </div>
                                ) : ( <p className="no-data-message">No data available for this chart.</p> )
                           )}
                           {!isLoading && !error && !simulationDataReady && <p className="no-data-message">Simulation results not available.</p>}
                      </div>
                     <div className="results-section content-card">
                         <h3 className="results-heading"> Results <span className="info-icon" title="Yield from Mushroom phase simulation. C:N Ratio fixed.">i</span> </h3>
                          <div className="results-items">
                              <div className="result-item">
                                  <span className="result-label">Projected Yield:</span>
                                  <span className="result-value"> {isLoading ? '...' : error ? 'Error' : Number.isFinite(finalSimulatedYield) ? `${finalSimulatedYield.toFixed(1)}g` : 'N/A'} </span>
                              </div>
                              <div className="result-item">
                                  <span className="result-label">Initial C:N Ratio:</span>
                                  <span className="result-value"> {FIXED_CN_RATIO.toFixed(2)} </span>
                              </div>
                          </div>
                      </div>
                 </div>
             </main>
         </div>
     );
};

export default GrowthSimulation;
