    };

    useEffect(() => {
        let isMounted = true;
        setIsLoading(true);
        setError(null);
        setFinalSimulatedYield(null);

        setTimeout(() => {
            if (!isMounted) return;
            try {
                // Load parameters (Batch Name and Start Date are correctly loaded here)
                const savedTemperature = localStorage.getItem(LOCAL_STORAGE_KEYS.TEMPERATURE);
                const savedPh = localStorage.getItem(LOCAL_STORAGE_KEYS.PH);
                const savedSubstrateJson = localStorage.getItem(LOCAL_STORAGE_KEYS.SUBSTRATE);
                const savedDays = localStorage.getItem(LOCAL_STORAGE_KEYS.SIMULATION_DAYS);
                const savedBatchName = localStorage.getItem(LOCAL_STORAGE_KEYS.BATCH_NAME) || 'N/A'; // Correctly reads batch name
                const savedStartDate = localStorage.getItem(LOCAL_STORAGE_KEYS.START_DATE) || 'N/A'; // Correctly reads start date

                setBatchName(savedBatchName); // State is set
                setStartDate(savedStartDate); // State is set

                // --- Parameter validation and simulation run (same as before) ---
                if (savedTemperature === null || savedPh === null || savedSubstrateJson === null || savedDays === null) {
                    const missing = [ /* ... error message construction ... */ ].filter(Boolean).join(', ');
                    throw new Error(`Missing required simulation parameters: ${missing}. Please set them in Condition Settings.`);
                }
                const temp = parseFloat(savedTemperature);
                const phValue = parseFloat(savedPh);
                const days = parseInt(savedDays, 10);
                let substrateData = null;
                let initialWeight = 0;
                try {
                    substrateData = JSON.parse(savedSubstrateJson);
                    if (typeof substrateData !== 'object' || substrateData === null) throw new Error("Invalid substrate format.");
                    initialWeight = Object.values(substrateData).reduce((sum, val) => sum + (Number(val) || 0), 0);
                } catch (parseError) { /* ... error handling ... */ throw parseError; }
                if (isNaN(temp) || isNaN(phValue) || isNaN(initialWeight) || isNaN(days)) throw new Error("Invalid numerical parameters.");
                const simulationDuration = (days > 0) ? days : defaultParams.timeLength;
                const simulationInputs = {
                    'External Temp': temp,
                    'pH': phValue,
                    'Initial Dry Substrate Weight': initialWeight > 0 ? initialWeight : defaultParams['Initial Dry Substrate Weight'],
                    'timeLength': simulationDuration,
                };
                const results = runSimulation(simulationInputs);
                // --- (End of parameter validation and simulation run) ---


                if (isMounted) {
                     setSimulationResults(results);
                     if (results && results.length > 0) {
                         const lastResult = results[results.length - 1];
                          if (lastResult && Number.isFinite(lastResult.MushroomYield)) {
                              setFinalSimulatedYield(lastResult.MushroomYield);
                          }
                          console.log("Simulation ended at t=", lastResult.time, " Final State:", lastResult);
                     } else {
                          console.log("Simulation produced no results.");
                     }
                 }

            } catch (err) {
                console.error("Error during simulation setup or run:", err);
                if (isMounted) setError(err.message || "Failed to run simulation.");
            } finally {
                if (isMounted) setIsLoading(false);
            }
        }, 50);

        return () => { isMounted = false; };
    }, []);

    const handleBackClick = () => { navigate('/condition-setting'); };

    // --- Chart Data & Options ---
    const getChartConfig = () => {
        if (!simulationResults || simulationResults.length === 0) {
            return { data: { datasets: [] }, options: {} };
        }

        const yieldColor = '#2E7D32';
        const tempColor = '#C62828';
        const myceliumColor = '#1976D2'; // Blue for Mycelium
        const textColor = '#333';
        const gridColor = 'rgba(0, 0, 0, 0.08)';

        let datasets = [];
        const options = {
            responsive: true, maintainAspectRatio: false,
            plugins: { /* ... legend, tooltip, title config ... */
                legend: { display: true, labels: { color: textColor, font: { family: 'Lexend'} } },
                tooltip: { enabled: true, bodyFont: { family: 'Lexend'}, titleFont: { family: 'Lexend' } },
                title: { display: true, text: '', color: textColor, font: { size: 16, family: 'Lexend' } }
             },
            scales: {
                x: { /* ... x-axis config ... */
                    type: 'linear', title: { display: true, text: 'Time (Days)', color: textColor, font: { family: 'Lexend'} },
                    ticks: { color: textColor, font: { family: 'Lexend'} }, grid: { color: gridColor }, min: 0,
                 },
                // Define ALL potential Y axes
                yYield: { type: 'linear', position: 'left', title: { display: true, text: 'Yield (g)', color: yieldColor, font: { family: 'Lexend'} }, ticks: { color: yieldColor, font: { family: 'Lexend'} }, grid: { drawOnChartArea: false }, beginAtZero: true, display: false }, // Initially hidden
                yTemp: { type: 'linear', position: 'left', title: { display: true, text: 'Temperature (째C)', color: tempColor, font: { family: 'Lexend'} }, ticks: { color: tempColor, font: { family: 'Lexend'} }, grid: { drawOnChartArea: false }, beginAtZero: false, display: false }, // Initially hidden
                yMycelium: { type: 'linear', position: 'left', title: { display: true, text: 'Mycelium Density', color: myceliumColor, font: { family: 'Lexend'} }, ticks: { color: myceliumColor, font: { family: 'Lexend'} }, grid: { drawOnChartArea: false }, beginAtZero: true, suggestedMax: 1, display: false } // Initially hidden
            }
        };

        // Reset display for all axes before setting the active one
        options.scales.yYield.display = false;
        options.scales.yTemp.display = false;
        options.scales.yMycelium.display = false;


        switch (activeChartTab) {
            case 'yield':
                options.plugins.title.text = 'Yield Over Time';
                options.scales.yYield.display = true; // Show this axis
                datasets = [{
                    label: 'Mushroom Yield (g)',
                    data: simulationResults.map(p => ({ x: p.time, y: Number.isFinite(p.MushroomYield) ? p.MushroomYield : null })),
                    borderColor: yieldColor, backgroundColor: 'rgba(46, 125, 50, 0.1)',
                    yAxisID: 'yYield',
                }];
                break;
            case 'temp':
                 options.plugins.title.text = 'Temperature Over Time';
                 options.scales.yTemp.display = true; // Show this axis
                 datasets = [{
                     label: 'Temperature (째C)',
                     data: simulationResults.map(p => ({ x: p.time, y: Number.isFinite(p.Temp) ? p.Temp : null })),
                     borderColor: tempColor, backgroundColor: 'rgba(198, 40, 40, 0.1)',
                     yAxisID: 'yTemp',
                 }];
                 break;
            // *** ADD CASE FOR MYCELIUM ***
            case 'mycelium':
                 options.plugins.title.text = 'Mycelium Growth Over Time';
                 options.scales.yMycelium.display = true; // Show this axis
                 datasets = [{
                     label: 'Mycelium Density', // Or 'Mycelium Growth'
                     data: simulationResults.map(p => ({ x: p.time, y: Number.isFinite(p.Mycelium) ? p.Mycelium : null })),
                     borderColor: myceliumColor, backgroundColor: 'rgba(25, 118, 210, 0.1)',
                     yAxisID: 'yMycelium',
                 }];
                 break;
            default:
                datasets = [];
        }

        return {
             data: { datasets: datasets.map(ds => ({ ...ds, tension: 0.1, pointRadius: 2, spanGaps: true })) },
             options: options
        };
    };

    const chartConfig = getChartConfig();

    // --- JSX ---
    return (
         <div className="growth-simulation">
             <header className="simulation-header">
                 <div className="logobox">
                      <button onClick={handleBackClick} className="back-button" title="Back to Settings">
                          &larr;
                      </button>
                     <div className="logo">
                         <img src={logoSrc} alt="City Sprouts Logo" />
                     </div>
                 </div>
                 {/* These values are correctly set from localStorage via useEffect */}
                 <div className="batch-info">
                     <span className="batch-name">Batch: #{batchName}</span>
                     <span className="start-date">Start Date: {startDate}</span>
                 </div>
             </header>

             <main className="main-content-grid">
                 <div className="animation-area content-card">
                     <span>Animation</span>
                 </div>

                 <div className="chart-results-column">
                     <div className="tabs-container">
                         <button
                             onClick={() => setActiveChartTab('yield')}
                             className={`tab-button ${activeChartTab === 'yield' ? 'tab-button-active' : ''}`}
                         > Yield over Time </button>
                         <button
                             onClick={() => setActiveChartTab('temp')}
                              className={`tab-button ${activeChartTab === 'temp' ? 'tab-button-active' : ''}`}
                         > Temperature </button>
                         {/* *** ADD MYCELIUM TAB BUTTON *** */}
                         <button
                             onClick={() => setActiveChartTab('mycelium')}
                              className={`tab-button ${activeChartTab === 'mycelium' ? 'tab-button-active' : ''}`}
                         > Mycelium </button>
                     </div>

                     <div className="chart-area content-card">
                          {/* Loading/Error/Chart Display Logic (no changes needed here) */}
                          {isLoading && <p className="loading-message">Loading simulation data...</p>}
                          {error && <p className="error-message">Error: {error}</p>}
                          {!isLoading && !error && simulationResults && (
                               chartConfig.data.datasets.length > 0 ? (
                                   <div className="chart-container"> <Line options={chartConfig.options} data={chartConfig.data} /> </div>
                               ) : ( <p className="no-data-message">No data available for this chart.</p> )
                          )}
                          {!isLoading && !error && !simulationResults && <p className="no-data-message">No simulation results available.</p>}
                     </div>

                     <div className="results-section content-card">
                          {/* Results Display Logic (no changes needed here) */}
                          <h3 className="results-heading"> Results <span className="info-icon" title="Key simulation outputs based on initial conditions">i</span> </h3>
                         <div className="results-items">
                             <div className="result-item">
                                 <span className="result-label">Projected Yield:</span>
                                 <span className="result-value">
                                     {isLoading ? '...' : error ? 'Error' : Number.isFinite(finalSimulatedYield) ? `${finalSimulatedYield.toFixed(1)}g` : 'N/A'}
                                 </span>
                             </div>
                             <div className="result-item">
                                 <span className="result-label">Initial C:N Ratio:</span>
                                 <span className="result-value">
                                     {FIXED_CN_RATIO.toFixed(2)}
                                 </span>
                             </div>
                         </div>
                     </div>
                 </div>
             </main>
         </div>
     );
};

export default GrowthSimulation;

