    if (x >= data[data.length - 1][0]) return data[data.length - 1][1];
    let i = 0;
    while (i < data.length - 1 && x > data[i + 1][0]) i++;
    const x0 = data[i][0], y0 = data[i][1], x1 = data[i + 1][0], y1 = data[i + 1][1];
    // Avoid division by zero if x0 and x1 are the same
    const divisor = (x1 - x0);
    if (divisor === 0) return y0; // Or y1, depending on desired behavior for duplicate x values
    const t = (x - x0) / divisor;
    return y0 + t * (y1 - y0);
}

