            // Set derivatives to zero to prevent solver failure, though results will be wrong
            dydt.fill(0);
        }
    };

    const integrator = odeRK4(y0, derivativeFuncForSolver, t0, positiveDt);
    const results = [{ time: integrator.t, state: [...integrator.y] }];

    console.log("Starting JS simulation...");
    if (numSteps <= 0) {
         console.warn("Simulation length or time step results in zero steps.");
         return []; // Return empty results if no steps to run
    }

    for (let i = 0; i < numSteps; i++) {
        try {
            integrator.step();
             // Check for NaN or Infinity in state, indicates instability
            if (integrator.y.some(val => !Number.isFinite(val))) {
                console.error("Simulation unstable: Non-finite number detected in state at t=", integrator.t);
