import React, { useState } from 'react';
import { Pie } from 'react-chartjs-2';
import '../styles/ConditionSetting.css';

const ConditionSetting = () => {
  const [temperature, setTemperature] = useState(25);
  const [ph, setPh] = useState(7);
  const [substrate, setSubstrate] = useState({
    riceBran: 25,
    corn: 25,
    sugarcane: 25,
    sawdust: 25,
  });

  const handleSubstrateChange = (key, value) => {
    setSubstrate((prev) => ({
      ...prev,
      [key]: value,
    }));
  };

  const pieData = {
    labels: ['Rice Bran', 'Corn', 'Sugarcane', 'Sawdust'],
    datasets: [
      {
        data: Object.values(substrate),
        backgroundColor: ['#FF914D', '#FFD700', '#7E9777', '#8B4513'],
      },
    ],
  };

  return (
    <div className="condition-setting">
      <header className="condition-header">
        <h1>Make Your Mushroom Grow</h1>
      </header>
      <main className="condition-content">
        <div className="sliders">
          <div>
            <label>Temperature: {temperature}Â°C</label>
            <input
              type="range"
              min="10"
              max="40"
              value={temperature}
              onChange={(e) => setTemperature(e.target.value)}
            />
          </div>
          <div>
            <label>pH: {ph}</label>
            <input
              type="range"
              min="3"
              max="10"
              step="0.1"
              value={ph}
              onChange={(e) => setPh(e.target.value)}
            />
          </div>
        </div>
        <div className="substrate-sliders">
          <h3>Substrate Composition:</h3>
          {Object.keys(substrate).map((key) => (
            <div key={key}>
              <label>
                {key.charAt(0).toUpperCase() + key.slice(1)}: {substrate[key]}%
              </label>
              <input
                type="range"
                min="0"
                max="100"
                value={substrate[key]}
                onChange={(e) =>
                  handleSubstrateChange(key, parseInt(e.target.value, 10))
                }
              />
            </div>
          ))}
        </div>
        <div className="pie-chart">
          <h3>Composition Chart:</h3>
          <Pie data={pieData} />
        </div>
        <button className="start-button">Start Growing</button>
      </main>
    </div>
  );
};

export default ConditionSetting;
