import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { Line } from 'react-chartjs-2';
import { Chart as ChartJS, LineElement, PointElement, Tooltip, Legend, CategoryScale, LinearScale, Title } from 'chart.js';
import odeRK4 from 'ode-rk4';
import '../styles/GrowthSimulation.css'; // Import the CSS file
import logoSrc from '../assets/images/cslogo.png';

// --- Simulation Logic & ChartJS Registration ---
const defaultParams = {
    timeStart: 0, timeLength: 100, timeStep: 0.2,
    'C BD Rate': 1.11, 'C Coeff': 0.7, 'C Cons. Rate': 76.39, 'CO2 Coeff': 0.8,
    'CO2 Production Rate': 410000, 'CO2 Removal Rate': 12.6, 'External CO2': 428.23,
    'External Temp': 24, 'Growth Coeff': 5.1,
    // 'Initial C:N Ratio': 43.58, // This default is overridden by FIXED_CN_RATIO
    'Initial Complex:Simple Ratio': 0.8054,
    'Initial Dry Substrate Weight': 410.2,
    'IsMush': 1, 'Mushroom Multiplier': 5.17, 'N Coeff': 68.4, 'N Rate': 13.03,
    'pH': 6.91,
    'pH Coeff': 1.4, 'Temp Coeff': 1.3, 'Temp Production Rate': 405,
    'Temp Transfer Rate': 1.5,
    'Effect of CO2 on Mush Growth Data': [[500, 0.9], [800, 1], [1000, 0.8], [1200, 0.25], [1400, 0]],
    'Effect of CO2 on Myc Growth Data': [[15000, 1], [20000, 0.9], [25000, 0.25], [30000, 0]],
    'Effect of pH on Mush Growth Data': [[5.5, 0], [6, 0.25], [6.7, 1], [7, 0.6], [7.5, 0]],
    'Effect of pH on Myc Growth Data': [[5.8, 0], [6, 0.25], [6.7, 1], [7, 0.6], [7.5, 0]],
    'Effect of Temp on Mush Growth Data': [[10, 0], [15, 5.37], [20, 6.36], [25, 8.63], [30, 6.37], [40, 0]],
    'Effect of Temp on Myc Growth Data': [[10, 0], [15, 5.37], [20, 6.36], [25, 8.63], [30, 6.37], [35, 5.36], [45, 0]],
};

const FIXED_CN_RATIO = 0.96; // User requested fixed value

// --- Simulation Functions (linearInterpolate, derivatives, getInitialState, runSimulation) ---
// KEEP THE FUNCTIONS FROM THE PREVIOUS STEP - they correctly use currentParams
// and the logic matches the model. Ensure they are present here.
function linearInterpolate(data, x) { /* ... */ }
 linearInterpolate = (data, x) => {
     if (!data || data.length === 0) return 0;
     if (x <= data[0][0]) return data[0][1];
     if (x >= data[data.length - 1][0]) return data[data.length - 1][1];
     let i = 0;
     while (i < data.length - 1 && x > data[i + 1][0]) i++;
     const x0 = data[i][0], y0 = data[i][1], x1 = data[i + 1][0], y1 = data[i + 1][1];
     const divisor = (x1 - x0);
     if (divisor === 0) return y0;
     const t = (x - x0) / divisor;
     return y0 + t * (y1 - y0);
 }


function derivatives(dydt, y, t, currentParams) { /* ... (same as before) ... */ }
 derivatives = (dydt, y, t, currentParams) => {
     const [ currentCO2, currentComplexC, currentMushroomYield, currentMycelium, currentN, currentSimpleC, currentSpace, currentTemp ] = y;
     const initialDrySubstrateWeight = currentParams['Initial Dry Substrate Weight'] || 1;
     const cnRatio = currentParams['Initial C:N Ratio']; // Uses the fixed value passed in
     const cnRatioPlusOne = cnRatio + 1;
     const InitC = initialDrySubstrateWeight - (initialDrySubstrateWeight / cnRatioPlusOne);
     const AvailableN = currentN / initialDrySubstrateWeight;
     const AvailableC = currentSimpleC / initialDrySubstrateWeight;
     const NEffect = currentParams['N Coeff'] * AvailableN;
     const CEffect = Math.sqrt(Math.max(0, currentParams['C Coeff'] * AvailableC));
     const VolumeNormalisation = Math.sqrt(100 / initialDrySubstrateWeight);
     const Effect_of_CO2_on_Mush_Growth = linearInterpolate(currentParams['Effect of CO2 on Mush Growth Data'], currentCO2);
     const Effect_of_CO2_on_Myc_Growth = linearInterpolate(currentParams['Effect of CO2 on Myc Growth Data'], currentCO2);
     const Effect_of_pH_on_Mush_Growth = linearInterpolate(currentParams['Effect of pH on Mush Growth Data'], currentParams['pH']);
     const Effect_of_pH_on_Myc_Growth = linearInterpolate(currentParams['Effect of pH on Myc Growth Data'], currentParams['pH']);
     const Effect_of_Temp_on_Mush_Growth = linearInterpolate(currentParams['Effect of Temp on Mush Growth Data'], currentTemp);
     const Effect_of_Temp_on_Myc_Growth = linearInterpolate(currentParams['Effect of Temp on Myc Growth Data'], currentTemp);
     const TempEff_ = currentParams['IsMush'] === 0 ? Effect_of_Temp_on_Myc_Growth : Effect_of_Temp_on_Mush_Growth;
     const TempEffect = (currentParams['Temp Coeff'] * TempEff_) / 10;
     const pHEff_ = currentParams['IsMush'] === 0 ? Effect_of_pH_on_Myc_Growth : Effect_of_pH_on_Mush_Growth;
     const pHEffect = pHEff_ * currentParams['pH Coeff'];
     const CO2Eff_ = currentParams['IsMush'] === 0 ? Effect_of_CO2_on_Myc_Growth : Effect_of_CO2_on_Mush_Growth;
     const CO2Effect = currentParams['CO2 Coeff'] * CO2Eff_;
     const RawGrowthRate = TempEffect * pHEffect * NEffect * CEffect * CO2Effect;
     const GrowthOpp = currentSpace * currentMycelium;
     let GrowthRate = currentParams['IsMush'] === 1 ? (RawGrowthRate * currentParams['Growth Coeff']) : (RawGrowthRate * GrowthOpp * currentParams['Growth Coeff']);
     GrowthRate = Math.max(0, GrowthRate);
     let NormGrowthRate = currentParams['IsMush'] === 1 ? (GrowthRate / 100) : GrowthRate;
     const flow_Breakdown = currentMycelium * currentParams['C BD Rate'] * currentComplexC * RawGrowthRate * VolumeNormalisation;
     const flow_CCons = NormGrowthRate * currentParams['C Cons. Rate'];
     const flow_CO2Prod = NormGrowthRate * currentParams['CO2 Production Rate'];
     const flow_CO2Vent = currentParams['CO2 Removal Rate'] * (currentCO2 - currentParams['External CO2']) * VolumeNormalisation;
     const flow_Colonisation = currentParams['IsMush'] === 0 ? GrowthRate : 0;
     const flow_MushroomGrowth = currentParams['IsMush'] === 1 ? (GrowthRate * currentParams['Mushroom Multiplier']) : 0;
     const flow_NCons = NormGrowthRate * currentParams['N Rate'];
     const flow_TempProd = NormGrowthRate * currentParams['Temp Production Rate'];
     const flow_TempVent = (currentTemp - currentParams['External Temp']) * currentParams['Temp Transfer Rate'] * VolumeNormalisation;
     dydt[0] = flow_CO2Prod - flow_CO2Vent; dydt[1] = -flow_Breakdown; dydt[2] = flow_MushroomGrowth; dydt[3] = flow_Colonisation;
     dydt[4] = -flow_NCons; dydt[5] = flow_Breakdown - flow_CCons; dydt[6] = -flow_Colonisation; dydt[7] = flow_TempProd - flow_TempVent;
     if (currentCO2 <= 0 && dydt[0] < 0) dydt[0] = 0; if (currentComplexC <= 0 && dydt[1] < 0) dydt[1] = 0; if (currentMushroomYield <= 0 && dydt[2] < 0) dydt[2] = 0; if (currentMycelium <= 0 && dydt[3] < 0) dydt[3] = 0; if (currentN <= 0 && dydt[4] < 0) dydt[4] = 0; if (currentSimpleC <= 0 && dydt[5] < 0) dydt[5] = 0; if (currentSpace <= 0 && dydt[6] < 0) dydt[6] = 0; if (currentTemp <= 0 && dydt[7] < 0) dydt[7] = 0;
 }


function getInitialState(p) { /* ... (same as before) ... */ }
 getInitialState = (p) => {
     const initialDrySubstrateWeight = Number(p['Initial Dry Substrate Weight']) || defaultParams['Initial Dry Substrate Weight'];
     const cnRatio = Number(p['Initial C:N Ratio']); // Use the fixed ratio passed in p
     const complexSimpleRatio = Number(p['Initial Complex:Simple Ratio']) || defaultParams['Initial Complex:Simple Ratio'];
     const cnRatioPlusOne = cnRatio + 1;
     const Init_C = initialDrySubstrateWeight - (initialDrySubstrateWeight / cnRatioPlusOne);
     return [
         Number(p['External CO2']) || defaultParams['External CO2'],
         Init_C * complexSimpleRatio,
         0,
         0.01, // Start Mycelium low
         initialDrySubstrateWeight / cnRatioPlusOne,
         Init_C * (1 - complexSimpleRatio),
         1 - 0.01, // Start Space high
         Number(p['External Temp']) || defaultParams['External Temp']
     ];
 }


function runSimulation(inputParams) { /* ... (same as before, forces FIXED_CN_RATIO) ... */ }
 runSimulation = (inputParams) => {
     const currentParams = {
         ...defaultParams,
         ...inputParams,
         'Initial C:N Ratio': FIXED_CN_RATIO, // *** Force the C:N Ratio ***
     };
     const y0 = getInitialState(currentParams);
     const t0 = currentParams.timeStart;
     const dt = currentParams.timeStep;
     const timeLength = Math.max(0, currentParams.timeLength);
     const positiveDt = Math.max(dt, 1e-6);
     const numSteps = Math.floor(timeLength / positiveDt);
     const derivativeFuncForSolver = (dydt, y, t) => {
         try {
             derivatives(dydt, y, t, currentParams);
         } catch (e) {
             console.error("Error in derivatives function:", e);
             dydt.fill(0);
         }
     };
     const integrator = odeRK4(y0, derivativeFuncForSolver, t0, positiveDt);
     const results = [{ time: integrator.t, state: [...integrator.y] }];
     console.log(`Starting JS simulation for ${timeLength} days with params:`, currentParams);
     if (numSteps <= 0) {
         console.warn("Simulation length/step results in zero steps.");
         return [];
     }
     for (let i = 0; i < numSteps; i++) {
         try {
             integrator.step();
             if (integrator.y.some(val => !Number.isFinite(val))) {
                 console.error("Simulation became unstable at t=", integrator.t, " State:", integrator.y);
             }
             results.push({ time: integrator.t, state: [...integrator.y] });
         } catch (solverError) {
             console.error("Error during RK4 step:", solverError);
             break;
         }
     }
     console.log("JS simulation finished.");
     return results.map(p => ({
         time: p.time, CO2: p.state[0], ComplexC: p.state[1], MushroomYield: p.state[2], Mycelium: p.state[3], N: p.state[4], SimpleC: p.state[5], Space: p.state[6], Temp: p.state[7]
     }));
 }



ChartJS.register(LineElement, PointElement, Tooltip, Legend, CategoryScale, LinearScale, Title);

// --- React Component ---
const GrowthSimulation = () => {
    const navigate = useNavigate();
    const [simulationResults, setSimulationResults] = useState(null);
    const [isLoading, setIsLoading] = useState(true);
    const [error, setError] = useState(null);
    const [batchName, setBatchName] = useState('');
    const [startDate, setStartDate] = useState('');
    // No state needed for C:N Ratio as it's fixed
    const [finalSimulatedYield, setFinalSimulatedYield] = useState(null);
    const [activeChartTab, setActiveChartTab] = useState('yield');

    // Use the consistent localStorage keys
    const LOCAL_STORAGE_KEYS = {
        TEMPERATURE: 'sim_temperature',
        PH: 'sim_ph',
        SUBSTRATE: 'sim_substrate', // Key for the substrate *object*
        SIMULATION_DAYS: 'sim_duration',
        BATCH_NAME: 'sim_batch_name',
        START_DATE: 'sim_start_date',
    };

    useEffect(() => {
        let isMounted = true;
        setIsLoading(true);
        setError(null);
        setFinalSimulatedYield(null);

        setTimeout(() => {
            if (!isMounted) return;
            try {
                // Load parameters using consistent keys
                const savedTemperature = localStorage.getItem(LOCAL_STORAGE_KEYS.TEMPERATURE);
                const savedPh = localStorage.getItem(LOCAL_STORAGE_KEYS.PH);
                const savedSubstrateJson = localStorage.getItem(LOCAL_STORAGE_KEYS.SUBSTRATE); // Read substrate object JSON
                const savedDays = localStorage.getItem(LOCAL_STORAGE_KEYS.SIMULATION_DAYS);
                const savedBatchName = localStorage.getItem(LOCAL_STORAGE_KEYS.BATCH_NAME) || 'N/A';
                const savedStartDate = localStorage.getItem(LOCAL_STORAGE_KEYS.START_DATE) || 'N/A';

                setBatchName(savedBatchName);
                setStartDate(savedStartDate);

                // Validate required parameters
                if (savedTemperature === null || savedPh === null || savedSubstrateJson === null || savedDays === null) {
                    const missing = [
                        savedTemperature === null && 'Temperature',
                        savedPh === null && 'pH',
                        savedSubstrateJson === null && 'Substrate Data',
                        savedDays === null && 'Simulation Duration'
                    ].filter(Boolean).join(', ');
                    throw new Error(`Missing required simulation parameters: ${missing}. Please set them in Condition Settings.`);
                }

                // Parse values
                const temp = parseFloat(savedTemperature);
                const phValue = parseFloat(savedPh);
                const days = parseInt(savedDays, 10);
                let substrateData = null;
                let initialWeight = 0;

                // Try parsing substrate JSON and calculate weight
                try {
                    substrateData = JSON.parse(savedSubstrateJson);
                    if (typeof substrateData !== 'object' || substrateData === null) {
                        throw new Error("Invalid substrate data format.");
                    }
                    // Calculate initial weight from the parsed substrate object
                    initialWeight = Object.values(substrateData).reduce((sum, val) => sum + (Number(val) || 0), 0);
                } catch (parseError) {
                     console.error("Failed to parse substrate data from localStorage:", parseError);
                     throw new Error("Invalid substrate data found. Please re-save conditions.");
                }


                if (isNaN(temp) || isNaN(phValue) || isNaN(initialWeight) || isNaN(days)) {
                    throw new Error("Invalid numerical parameters loaded from localStorage.");
                }

                const simulationDuration = (days > 0) ? days : defaultParams.timeLength;

                // Prepare inputs for the simulation function
                const simulationInputs = {
                    'External Temp': temp,
                    'pH': phValue,
                    // Pass the calculated initial weight
                    'Initial Dry Substrate Weight': initialWeight > 0 ? initialWeight : defaultParams['Initial Dry Substrate Weight'],
                    'timeLength': simulationDuration,
                    // C:N Ratio is fixed inside runSimulation
                };

                // Run the simulation
                const results = runSimulation(simulationInputs);

                if (isMounted) {
                     setSimulationResults(results);
                     if (results && results.length > 0) {
                         const lastResult = results[results.length - 1];
                          if (lastResult && Number.isFinite(lastResult.MushroomYield)) {
                              setFinalSimulatedYield(lastResult.MushroomYield);
                          }
                          console.log("Simulation ended at t=", lastResult.time, " Final State:", lastResult);
                     } else {
                          console.log("Simulation produced no results.");
                     }
                 }

            } catch (err) {
                console.error("Error during simulation setup or run:", err);
                if (isMounted) setError(err.message || "Failed to run simulation.");
            } finally {
                if (isMounted) setIsLoading(false);
            }
        }, 50);

        return () => { isMounted = false; };
    }, []);

    const handleBackClick = () => { navigate('/condition-setting'); };

    // --- Chart Data & Options --- (Keep function from previous step)
    const getChartConfig = () => { /* ... (same as before) ... */ };
     getChartConfig = () => {
         if (!simulationResults || simulationResults.length === 0) {
             return { data: { datasets: [] }, options: {} };
         }
         const yieldColor = '#2E7D32';
         const tempColor = '#C62828';
         const textColor = '#333';
         const gridColor = 'rgba(0, 0, 0, 0.08)';
         let datasets = [];
         const options = {
             responsive: true, maintainAspectRatio: false,
             plugins: {
                 legend: { display: true, labels: { color: textColor, font: { family: 'Lexend'} } },
                 tooltip: { enabled: true, bodyFont: { family: 'Lexend'}, titleFont: { family: 'Lexend' } },
                 title: { display: true, text: '', color: textColor, font: { size: 16, family: 'Lexend' } }
             },
             scales: {
                 x: { type: 'linear', title: { display: true, text: 'Time (Days)', color: textColor, font: { family: 'Lexend'} }, ticks: { color: textColor, font: { family: 'Lexend'} }, grid: { color: gridColor }, min: 0, },
                 yYield: { type: 'linear', position: 'left', title: { display: true, text: 'Yield (g)', color: yieldColor, font: { family: 'Lexend'} }, ticks: { color: yieldColor, font: { family: 'Lexend'} }, grid: { drawOnChartArea: false }, beginAtZero: true, display: false },
                 yTemp: { type: 'linear', position: 'left', title: { display: true, text: 'Temperature (°C)', color: tempColor, font: { family: 'Lexend'} }, ticks: { color: tempColor, font: { family: 'Lexend'} }, grid: { drawOnChartArea: false }, beginAtZero: false, display: false }
             }
         };
         switch (activeChartTab) {
             case 'yield':
                 options.plugins.title.text = 'Yield Over Time'; options.scales.yYield.display = true;
                 datasets = [{ label: 'Mushroom Yield (g)', data: simulationResults.map(p => ({ x: p.time, y: Number.isFinite(p.MushroomYield) ? p.MushroomYield : null })), borderColor: yieldColor, backgroundColor: 'rgba(46, 125, 50, 0.1)', yAxisID: 'yYield', }];
                 break;
             case 'temp':
                  options.plugins.title.text = 'Temperature Over Time'; options.scales.yTemp.display = true;
                  datasets = [{ label: 'Temperature (°C)', data: simulationResults.map(p => ({ x: p.time, y: Number.isFinite(p.Temp) ? p.Temp : null })), borderColor: tempColor, backgroundColor: 'rgba(198, 40, 40, 0.1)', yAxisID: 'yTemp', }];
                  break;
             default: datasets = [];
         }
         return { data: { datasets: datasets.map(ds => ({ ...ds, tension: 0.1, pointRadius: 2, spanGaps: true })) }, options: options };
     };



    const chartConfig = getChartConfig();

    // --- JSX --- (Keep JSX structure from previous step)
    return (
         <div className="growth-simulation">
             <header className="simulation-header">
                 <div className="logobox">
                     <button onClick={handleBackClick} className="back-button" title="Back to Settings">
                          &larr;
                      </button>
                     <div className="logo">
                         <img src={logoSrc} alt="City Sprouts Logo" />
                     </div>
                 </div>
                 <div className="batch-info">
                     <span className="batch-name">Batch: #{batchName}</span>
                     <span className="start-date">Start Date: {startDate}</span>
                 </div>
             </header>

             <main className="main-content-grid">
                 <div className="animation-area content-card">
                     <span>Animation</span>
                 </div>

                 <div className="chart-results-column">
                     <div className="tabs-container">
                         <button
                             onClick={() => setActiveChartTab('yield')}
                             className={`tab-button ${activeChartTab === 'yield' ? 'tab-button-active' : ''}`}
                         > Yield over Time </button>
                         <button
                             onClick={() => setActiveChartTab('temp')}
                              className={`tab-button ${activeChartTab === 'temp' ? 'tab-button-active' : ''}`}
                         > Temperature </button>
                     </div>

                     <div className="chart-area content-card">
                          {isLoading && <p className="loading-message">Loading simulation data...</p>}
                          {error && <p className="error-message">Error: {error}</p>}
                          {!isLoading && !error && simulationResults && (
                               chartConfig.data.datasets.length > 0 ? (
                                   <div className="chart-container"> <Line options={chartConfig.options} data={chartConfig.data} /> </div>
                               ) : ( <p className="no-data-message">No data available for this chart.</p> )
                          )}
                          {!isLoading && !error && !simulationResults && <p className="no-data-message">No simulation results available.</p>}
                     </div>

                     <div className="results-section content-card">
                         <h3 className="results-heading"> Results <span className="info-icon" title="Key simulation outputs based on initial conditions">i</span> </h3>
                         <div className="results-items">
                             <div className="result-item">
                                 <span className="result-label">Projected Yield:</span>
                                 <span className="result-value">
                                     {isLoading ? '...' : error ? 'Error' : Number.isFinite(finalSimulatedYield) ? `${finalSimulatedYield.toFixed(1)}g` : 'N/A'}
                                 </span>
                             </div>
                             <div className="result-item">
                                 <span className="result-label">Initial C:N Ratio:</span>
                                 <span className="result-value">
                                     {FIXED_CN_RATIO.toFixed(2)} {/* Display fixed value */}
                                 </span>
                             </div>
                         </div>
                     </div>
                 </div>
             </main>
         </div>
     );
};

export default GrowthSimulation;
