import React, { useState, useEffect } from 'react';
import { Pie } from 'react-chartjs-2';
import { Chart as ChartJS, ArcElement, Tooltip, Legend } from 'chart.js';
import { useNavigate } from 'react-router-dom';
// Removed the specific CSS import, assuming Tailwind is used globally or setup elsewhere
// import '../styles/ConditionSetting.css';

// Register required components
ChartJS.register(ArcElement, Tooltip, Legend);

const ConditionSetting = () => {
    const navigate = useNavigate();

    // --- State Variables ---
    const [temperature, setTemperature] = useState(25);
    const [ph, setPh] = useState(7.0); // Use float for step
    const [substrate, setSubstrate] = useState({
        riceBran: 25,
        corn: 25,
        sugarcane: 25,
        sawdust: 25,
    });
    const [totalSubstrate, setTotalSubstrate] = useState(100); // Renamed from totalYield for clarity
    const [simulationDays, setSimulationDays] = useState(40);
    const [batchName, setBatchName] = useState(''); // New state
    const [startDate, setStartDate] = useState(''); // New state

    // --- Labels ---
    const substrateLabels = {
        riceBran: 'Rice Bran',
        corn: 'Corn Cob',
        sugarcane: 'Sugarcane',
        sawdust: 'Sawdust',
    };

    // --- Event Handlers ---
    const handleStartClick = () => {
        // Save relevant parameters to localStorage
        // Saving only simulation inputs, not batch name/date unless needed later
        localStorage.setItem('substrate', JSON.stringify(substrate));
        localStorage.setItem('temperature', temperature);
        localStorage.setItem('ph', ph);
        localStorage.setItem('yield', totalSubstrate); // Save total substrate weight as 'yield' for compatibility
        localStorage.setItem('simulationDays', simulationDays);
        console.log('Saved to localStorage:', { substrate, temperature, ph, totalSubstrate, simulationDays });
        navigate('/growth-simulation');
    };

    const handleBackClick = () => {
        navigate('/'); // Navigate back to the Landing Page
    };

    // Generic handler for number inputs
    const handleNumberChange = (setter, min, max, value, allowFloat = false) => {
         const numericValue = allowFloat ? parseFloat(value) : parseInt(value, 10);
         if (isNaN(numericValue)) {
            setter(allowFloat ? min : Math.round(min)); // Or set to empty/previous valid state
         } else if (numericValue >= min && numericValue <= max) {
             setter(numericValue);
         } else if (numericValue < min) {
             setter(min);
         } else {
             setter(max);
         }
    };

     const handleSubstrateChange = (key, value) => {
        const numericValue = parseInt(value, 10);
         if (isNaN(numericValue) || numericValue < 0) {
            setSubstrate((prev) => ({ ...prev, [key]: 0 }));
         } else {
             setSubstrate((prev) => ({ ...prev, [key]: numericValue }));
         }
    };


    // --- Effects ---
    useEffect(() => {
        // Calculate the total substrate weight
        const total = Object.values(substrate).reduce((sum, weight) => sum + (Number(weight) || 0), 0);
        setTotalSubstrate(total);
    }, [substrate]);

    // --- Chart Configuration ---
    const pieOptions = {
        responsive: true, // Make chart responsive
        maintainAspectRatio: false, // Allow aspect ratio changes
        plugins: {
            legend: {
                position: 'bottom', // Position legend below chart
                labels: {
                    color: '#6b4f4b', // Dark brown text for legend (adjust color)
                    font: { size: 12 },
                    boxWidth: 15, // Size of color box
                    padding: 15 // Padding around legend items
                }
            },
        },
    };

    const pieData = {
        labels: Object.values(substrateLabels),
        datasets: [
            {
                data: totalSubstrate > 0 ? Object.values(substrate) : [1], // Show grey circle if total is 0
                backgroundColor: totalSubstrate > 0
                    ? ['#e1c699', '#7E9777', '#FF914D', '#8B4513'] // Example colors (adjust)
                    : ['#d3d3d3'], // Grey color if no data
                borderColor: '#f5f2e9', // Match background or a subtle border
                borderWidth: totalSubstrate > 0 ? 2 : 0,
            },
        ],
    };

    // --- JSX ---
    return (
        // Main container with creamy background
        <div className="condition-setting min-h-screen bg-[#f5f2e9] p-4 md:p-8 text-[#6b4f4b]"> {/* Creamy background, dark brown text */}
            <header className="flex items-center justify-between mb-6 md:mb-10">
                {/* Styled Back Button */}
                <button
                    className="bg-[#d4b996] hover:bg-[#c8a980] text-[#6b4f4b] font-bold py-2 px-4 rounded-lg shadow transition duration-200"
                    onClick={handleBackClick}
                >
                    ← Back
                </button>
                {/* Title - centered (implicitly by justify-between and no other center element) */}
                <h1 className="text-2xl md:text-4xl font-bold text-center text-[#8B4513]"> {/* Darker brown for title */}
                    Set Your Growing Conditions!
                </h1>
                {/* Invisible spacer to balance the back button */}
                 <div className="w-16"></div> {/* Adjust width to match button approx */}
            </header>

            {/* Main content area - flex layout for columns */}
            <main className="flex flex-col md:flex-row gap-6 md:gap-10">

                {/* Left Column: Conditions & Substrate Inputs */}
                <div className="w-full md:w-1/3 space-y-6">
                    {/* Environmental Conditions Card */}
                    <div className="bg-white/70 backdrop-blur-sm p-5 rounded-xl shadow-md border border-gray-200">
                        <h3 className="text-lg font-semibold mb-4 flex items-center">
                            Environmental Conditions
                            <span className="ml-2 text-xs text-gray-500 cursor-help" title="Set temperature and pH for growth">(i)</span>
                        </h3>
                        <div className="space-y-3">
                            {/* Temperature Input */}
                            <div className="flex items-center justify-between">
                                <label htmlFor="temperature" className="block text-sm font-medium mr-2">Temperature (°C):</label>
                                <input
                                    id="temperature"
                                    type="number"
                                    min="20" max="30" step="1"
                                    value={temperature}
                                    onChange={(e) => handleNumberChange(setTemperature, 20, 30, e.target.value)}
                                    className="w-20 p-2 border border-gray-300 rounded-md shadow-sm focus:ring-[#c8a980] focus:border-[#c8a980] text-sm"
                                />
                            </div>
                             {/* pH Input */}
                            <div className="flex items-center justify-between">
                                <label htmlFor="ph" className="block text-sm font-medium mr-2">pH:</label>
                                <input
                                    id="ph"
                                    type="number"
                                    min="6.0" max="7.0" step="0.1"
                                    value={ph}
                                    onChange={(e) => handleNumberChange(setPh, 6.0, 7.0, e.target.value, true)}
                                    className="w-20 p-2 border border-gray-300 rounded-md shadow-sm focus:ring-[#c8a980] focus:border-[#c8a980] text-sm"
                                />
                            </div>
                        </div>
                    </div>

                    {/* Substrate Composition Card */}
                    <div className="bg-white/70 backdrop-blur-sm p-5 rounded-xl shadow-md border border-gray-200">
                         <h3 className="text-lg font-semibold mb-4 flex items-center">
                            Substrate Composition
                            <span className="ml-2 text-xs text-gray-500 cursor-help" title="Enter weight in grams for each component">(i)</span>
                         </h3>
                        <div className="space-y-3">
                            {Object.keys(substrate).map((key) => (
                                <div key={key} className="flex items-center justify-between">
                                    <label htmlFor={`substrate-${key}`} className="block text-sm font-medium mr-2">
                                        {substrateLabels[key]} (g):
                                    </label>
                                    <input
                                        id={`substrate-${key}`}
                                        type="number" min="0" value={substrate[key]}
                                        onChange={(e) => handleSubstrateChange(key, e.target.value)}
                                        className="w-20 p-2 border border-gray-300 rounded-md shadow-sm focus:ring-[#c8a980] focus:border-[#c8a980] text-sm"
                                    />
                                </div>
                            ))}
                             {/* Display Total Substrate */}
                             <div className="text-right font-semibold pt-2 border-t border-gray-200 mt-3">
                                Total: {totalSubstrate}g
                             </div>
                        </div>
                    </div>
                </div>

                {/* Right Column: Chart & Other Inputs */}
                <div className="w-full md:w-2/3 space-y-6">
                     {/* Chart and Other Details Card */}
                     <div className="bg-white/70 backdrop-blur-sm p-5 rounded-xl shadow-md border border-gray-200">
                        <h3 className="text-lg font-semibold mb-4 text-center">Your Substrate Composition</h3>
                        {/* Chart Container */}
                        <div className="w-full h-56 md:h-64 mx-auto mb-4"> {/* Fixed height container */}
                             {totalSubstrate > 0 ? (
                                <Pie data={pieData} options={pieOptions} />
                            ) : (
                                <div className="flex items-center justify-center h-full text-gray-500">
                                    Enter substrate values to see chart.
                                </div>
                            )}
                        </div>

                        {/* Additional Inputs */}
                        <div className="grid grid-cols-1 sm:grid-cols-2 gap-4">
                             <div>
                                <label htmlFor="batchName" className="block text-sm font-medium mb-1">Batch Name:</label>
                                <input
                                    id="batchName"
                                    type="text"
                                    value={batchName}
                                    onChange={(e) => setBatchName(e.target.value)}
                                    placeholder="e.g., Spring Batch 1"
                                    className="w-full p-2 border border-gray-300 rounded-md shadow-sm focus:ring-[#c8a980] focus:border-[#c8a980] text-sm"
                                />
                            </div>
                             <div>
                                <label htmlFor="startDate" className="block text-sm font-medium mb-1">Start Date:</label>
                                <input
                                    id="startDate"
                                    type="date" // Use date type for better UX
                                    value={startDate}
                                    onChange={(e) => setStartDate(e.target.value)}
                                    className="w-full p-2 border border-gray-300 rounded-md shadow-sm focus:ring-[#c8a980] focus:border-[#c8a980] text-sm"
                                />
                            </div>
                             <div className="sm:col-span-2"> {/* Span across two columns on small screens */}
                                <label htmlFor="simulationDays" className="block text-sm font-medium mb-1">Cultivation Duration (Days):</label>
                                <input
                                    id="simulationDays"
                                    type="number"
                                    min="1" max="365" step="1"
                                    value={simulationDays}
                                    onChange={handleDaysChange}
                                     className="w-full p-2 border border-gray-300 rounded-md shadow-sm focus:ring-[#c8a980] focus:border-[#c8a980] text-sm"
                                />
                            </div>
                        </div>
                    </div>
                </div>
            </main>

             {/* Start Growing Button - Centered at bottom */}
            <footer className="mt-8 text-center">
                 <button
                    className="bg-[#8B4513] hover:bg-[#7a3c10] text-white font-bold py-3 px-8 rounded-lg shadow-md transition duration-200 text-lg"
                    onClick={handleStartClick}
                >
                    Start Growing
                </button>
            </footer>
        </div>
    );
};

export default ConditionSetting;
